import React from "react";

const refresherProducts = [
  { id: 3, name: "Refresher Lemon", price: 4 },
  { id: 4, name: "Refresher Berry", price: 4.5 },
  // add more refresher coffees
];

export default function Refresher({ addToCart }) {
  return (
    <div>
      <h1>Refresher Menu</h1>
      <ul>
        {refresherProducts.map((product) => (
          <li key={product.id} style={{ marginBottom: "1rem" }}>
            {product.name} - ${product.price.toFixed(2)}{" "}
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
