import React from "react";
import main from "./assets/main.jpg";
import coffeeLogo from "./assets/jasmine.png";
import CircularText from "./CircularText";
import DualText from "./DualText";
import SimpleNavbar from "./SimpleNavbar";
import { Link } from "react-router-dom";

export default function Home() {
  const btnStyle = {
    padding: "10px",
    background: "#947848",
    color: "wheat",
    borderRadius: "5px",
    width: "200px",
    border: "2px solid transparent",
    fontFamily: "Arial"
  };

  return (
    <div style={{ display: "flex", padding: "1rem", fontFamily: "sans-serif" }}>
      {/* Left image */}
      <img
        src={main}
        alt="Coffee Shop Main"
        style={{
          width: "50%",
          height: "auto",
          borderRadius: "10px"
        }}
      />

      {/* Right content */}
      <div style={{ width: "50%", textAlign: "center", padding: "1rem" }}>
        <SimpleNavbar />

        {/* Circular text and logo */}
        <div style={{ position: "relative", width: "260px", height: "260px", margin: "0 auto" }}>
          {/* Top circular text */}
          <div style={{ position: "absolute", top: 0, left: 0 }}>
            <CircularText text="The Daily grind " radius={120} startAngle={-120} />
          </div>

          {/* Center logo */}
          <img
            src={coffeeLogo}
            alt="Coffee Logo"
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              width: "100px",
              height: "100px",
              transform: "translate(-50%, -50%)",
              borderRadius: "50%",
            }}
          />

          <div style={{ position: "absolute", top: 100, left: 0 }}>
            <CircularText text=" Coffee house" radius={120} startAngle={60} />
          </div>
        </div>

        {/* Order Now */}
        <p style={{ marginTop: "2rem", fontSize: "14px" }}>
          When placing an order, select the option<br />
          <b>"Contactless delivery"</b> and the courier will leave your order at the door.
        </p>
        <Link to="/coffee">
          <button style={btnStyle}>ORDER NOW</button>
        </Link>
      </div>
    </div>
  );
}
