import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";

import Home from "./Home";
import CoffeeShop from "./CoffeeShop";
import Espresso from "./Espresso";
import Refresher from "./Refresher";
import About from "./About";
import Service from "./Service";
import AddToCart from "./AddToCart";
import Payment from "./Payment";
import History from "./History";
import "./App.css";

function Navbar({ totalItems }) {
  const location = useLocation();
  return (
    <nav style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}>
      <Link to="/" style={{ fontWeight: location.pathname === "/" ? "bold" : "normal" }}>
        Home
      </Link>
      <Link to="/coffee" style={{ fontWeight: location.pathname === "/coffee" ? "bold" : "normal" }}>
        Coffee Shop
      </Link>
      <Link to="/about" style={{ fontWeight: location.pathname === "/about" ? "bold" : "normal" }}>
        About
      </Link>
      <Link to="/service" style={{ fontWeight: location.pathname === "/service" ? "bold" : "normal" }}>
        Service
      </Link>
      <Link to="/history" style={{ fontWeight: location.pathname === "/history" ? "bold" : "normal" }}>
        Order History
      </Link>
      <Link to="/cart" style={{ marginLeft: "auto" }}>
        ðŸ›’ Cart ({totalItems})
      </Link>
    </nav>
  );
}

function App() {
  // Initialize cart: group by category with empty arrays if none saved
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart ? JSON.parse(savedCart) : { espresso: [], refresher: [] };
  });

  // Initialize order history from localStorage or empty
  const [orderHistory, setOrderHistory] = useState(() => {
    const savedHistory = localStorage.getItem("orderHistory");
    return savedHistory ? JSON.parse(savedHistory) : [];
  });

  // Save cart on any change
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // Save order history on any change
  useEffect(() => {
    localStorage.setItem("orderHistory", JSON.stringify(orderHistory));
  }, [orderHistory]);

  // Add one item to the cart in the correct category
  const addToCart = (item, category) => {
    setCart((prev) => {
      const categoryItems = prev[category] || [];
      const exists = categoryItems.find((i) => i.id === item.id);

      let updatedCategory;
      if (exists) {
        updatedCategory = categoryItems.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        updatedCategory = [...categoryItems, { ...item, quantity: 1 }];
      }

      return { ...prev, [category]: updatedCategory };
    });
  };

  // Add multiple items (for history re-order)
  const addMultipleToCart = (items, category) => {
    setCart((prev) => {
      let updatedCategory = [...(prev[category] || [])];
      items.forEach((item) => {
        const exists = updatedCategory.find((i) => i.id === item.id);
        if (exists) {
          updatedCategory = updatedCategory.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + item.quantity } : i
          );
        } else {
          updatedCategory.push(item);
        }
      });
      return { ...prev, [category]: updatedCategory };
    });
  };

  // Remove an item from a specific category
  const removeFromCart = (id, category) => {
    setCart((prev) => {
      const updatedCategory = prev[category].filter((item) => item.id !== id);
      return { ...prev, [category]: updatedCategory };
    });
  };

  // Add a completed order to history
  const addOrderToHistory = (order) => {
    setOrderHistory((prev) => [...prev, order]);
  };

  // Clear the cart (empty all categories)
  const clearCart = () => setCart({ espresso: [], refresher: [] });

  // Calculate total items count across all categories
  const totalItems = Object.values(cart).flat().reduce((sum, item) => sum + item.quantity, 0);

  return (
    <Router>
      <Navbar totalItems={totalItems} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/coffee" element={<CoffeeShop />} />
        <Route
          path="/espresso"
          element={<Espresso addToCart={(item) => addToCart(item, "espresso")} />}
        />
        <Route
          path="/refresher"
          element={<Refresher addToCart={(item) => addToCart(item, "refresher")} />}
        />
        <Route path="/about" element={<About />} />
        <Route path="/service" element={<Service />} />
        <Route
          path="/cart"
          element={<AddToCart cart={cart} removeFromCart={removeFromCart} />}
        />
        <Route
          path="/payment"
          element={
            <Payment
              cart={cart}
              addOrderToHistory={addOrderToHistory}
              clearCart={clearCart}
            />
          }
        />
        <Route
          path="/history"
          element={<History orderHistory={orderHistory} addMultipleToCart={addMultipleToCart} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
