import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";

import CartIcon from "./CartIcon";
import AddToCart from "./AddToCart"; // Your AddToCart component here

export default function CoffeeShop() {
  const navigate = useNavigate();

  // Cart state - simple example; you'd want to lift this up or use context in a real app
  const [cart, setCart] = useState({
    espresso: [],
    refresher: [],
    tea: [],
    pastry: [],
    blended: [],
  });

  const [cartOpen, setCartOpen] = useState(false);

  // Add an item to cart example function
  const addToCart = (category, item) => {
    setCart((prevCart) => {
      const categoryItems = prevCart[category] || [];
      const existingIndex = categoryItems.findIndex((i) => i.id === item.id);

      if (existingIndex >= 0) {
        // Increase quantity if item exists
        const updatedItems = [...categoryItems];
        updatedItems[existingIndex] = {
          ...updatedItems[existingIndex],
          quantity: updatedItems[existingIndex].quantity + 1,
        };
        return { ...prevCart, [category]: updatedItems };
      } else {
        // Add new item
        return {
          ...prevCart,
          [category]: [...categoryItems, { ...item, quantity: 1 }],
        };
      }
    });
  };

  // Remove from cart
  const removeFromCart = (id, category) => {
    setCart((prevCart) => {
      const updatedItems = (prevCart[category] || []).filter(
        (item) => item.id !== id
      );
      return { ...prevCart, [category]: updatedItems };
    });
  };

  // Update quantity in cart
  const updateQuantity = (id, category, quantity) => {
    if (quantity < 1) return; // no zero or negative quantity

    setCart((prevCart) => {
      const updatedItems = (prevCart[category] || []).map((item) =>
        item.id === id ? { ...item, quantity } : item
      );
      return { ...prevCart, [category]: updatedItems };
    });
  };

  // Count total items in cart for icon badge
  const totalItems = Object.values(cart).reduce(
    (sum, items) => sum + items.reduce((s, i) => s + i.quantity, 0),
    0
  );

  const products = [
    { key: "espresso", label: "Espresso", img: espressoImg },
    { key: "refresher", label: "Refresher", img: refresherImg },
    { key: "tea", label: "Tea", img: teaImg },
    { key: "pastry", label: "Pastry", img: pastryImg },
    { key: "blended", label: "Blended", img: blendedImg },
  ];

  return (
    <div style={{ padding: "2rem", maxWidth: "900px", margin: "auto" }}>
      <h2 style={{ color: "#5a3e1b", textAlign: "center" }}>Our Coffee Menu</h2>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
          gap: "2rem",
          marginBottom: "2rem",
        }}
      >
        {products.map(({ key, label, img }) => (
          <button
            key={key}
            onClick={() => navigate(`/${key}`)}
            style={{
              backgroundColor: "#fff",
              border: "1px solid #ccc",
              borderRadius: "12px",
              padding: "10px",
              cursor: "pointer",
              boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
            }}
            aria-label={`View ${label}`}
            title={`View ${label}`}
          >
            <img
              src={img}
              alt={label}
              style={{ width: "100px", height: "100px", objectFit: "cover" }}
            />
            <span
              style={{ fontWeight: "bold", fontSize: "16px", color: "#5a3e1b" }}
            >
              {label}
            </span>
          </button>
        ))}
      </div>

      {/* Cart Icon fixed bottom-right */}
      <div
        style={{
          position: "fixed",
          bottom: "20px",
          right: "20px",
          cursor: "pointer",
          zIndex: 999,
        }}
        onClick={() => setCartOpen((open) => !open)}
        aria-label="Open Cart"
        title="Open Cart"
        role="button"
        tabIndex={0}
        onKeyDown={(e) => {
          if (e.key === "Enter" || e.key === " ") {
            e.preventDefault();
            setCartOpen((open) => !open);
          }
        }}
      >
        <CartIcon totalItems={totalItems} />
      </div>

      {/* AddToCart modal/panel */}
      {cartOpen && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            height: "100vh",
            width: "100vw",
            backgroundColor: "rgba(0,0,0,0.5)",
            zIndex: 1000,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
          onClick={() => setCartOpen(false)} // close modal on background click
        >
          <div
            onClick={(e) => e.stopPropagation()} // prevent modal close on inside click
            style={{
              backgroundColor: "white",
              borderRadius: "8px",
              width: "90%",
              maxWidth: "600px",
              maxHeight: "80vh",
              overflowY: "auto",
              padding: "1rem",
              boxShadow: "0 0 10px rgba(0,0,0,0.3)",
            }}
          >
            <AddToCart
              cart={cart}
              removeFromCart={removeFromCart}
              updateQuantity={updateQuantity}
            />
            <button
              onClick={() => setCartOpen(false)}
              style={{
                marginTop: "1rem",
                padding: "8px 12px",
                backgroundColor: "#5a3e1b",
                color: "white",
                border: "none",
                borderRadius: "6px",
                cursor: "pointer",
              }}
            >
              Close Cart
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
