import React from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";
import CartIcon from "./CartIcon"; // assume you have this component
import "./App.css";

export default function CoffeeShop({ addToCart, totalItems }) {
  const navigate = useNavigate();

  const products = [
    { id: 1, key: "espresso", label: "Espresso", img: espressoImg, category: "espresso" },
    { id: 2, key: "refresher", label: "Refresher", img: refresherImg, category: "refresher" },
    { id: 3, key: "tea", label: "Tea", img: teaImg, category: "tea" },
    { id: 4, key: "pastry", label: "Pastry", img: pastryImg, category: "pastry" },
    { id: 5, key: "blended", label: "Blended Beverage", img: blendedImg, category: "blended" },
  ];

  return (
    <div style={{ textAlign: "center", paddingBottom: "80px" }}>
      {/* Show cart icon with total items */}
      <div style={{ position: "fixed", top: 20, right: 20, zIndex: 1000 }}>
        <CartIcon totalItems={totalItems} onClick={() => navigate("/cart")} />
      </div>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(5, 1fr)",
          gap: "2rem",
          maxWidth: "900px",
          margin: "1rem auto",
        }}
      >
        {products.map(({ id, label, img, category }) => (
          <div key={id} style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
            <button
              onClick={() => navigate(`/${category}`)}
              style={{
                border: "none",
                width: "140px",
                height: "260px",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
                justifyContent: "flex-start",
                cursor: "pointer",
                background: "#f9f6f1",
                borderRadius: "15px",
                boxShadow: "0 4px 8px rgba(0,0,0,0.15)",
                padding: "10px",
              }}
            >
              <img
                src={img}
                alt={label}
                style={{ width: "120px", height: "120px", borderRadius: "15px", marginBottom: "10px" }}
              />
              <div
                style={{
                  fontFamily: "'Sansita Swashed', cursive",
                  fontSize: "1.1rem",
                  fontWeight: "700",
                  color: "#5a3e1b",
                  background: "#A17557",
                  borderRadius: "8px",
                  padding: "8px",
                  border: "2px solid #4b3715",
                  width: "100%",
                  textAlign: "center",
                  userSelect: "none",
                }}
              >
                {label}
              </div>
            </button>

            {/* Add to Cart button */}
            <button
              onClick={() =>
                addToCart({ id, label, price: 5 /* example price */, quantity: 1 }, category)
              }
              style={{
                marginTop: "10px",
                backgroundColor: "#4b3715",
                color: "white",
                border: "none",
                borderRadius: "8px",
                padding: "8px 12px",
                cursor: "pointer",
                fontWeight: "600",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
