import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart, updateQuantity }) {
  const navigate = useNavigate();

  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items) ? items.map((item) => ({ ...item, category })) : []
  );

  const [selectedItems, setSelectedItems] = useState(new Set());

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      newSet.has(key) ? newSet.delete(key) : newSet.add(key);
      return newSet;
    });
  };

  const handleQuantityChange = (item, newQty) => {
    const quantity = parseInt(newQty, 10);
    if (!isNaN(quantity) && quantity > 0) {
      updateQuantity(item.id, item.category, quantity);
    }
  };

  const selectedArray = allItems.filter((item) =>
    selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    navigate("/payment", { state: { selectedItems: selectedArray } });

    selectedArray.forEach((item) => {
      removeFromCart(item.id, item.category);
    });
  };

  const handleGoToHistory = () => {
    navigate("/history");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif", maxWidth: "800px", margin: "auto" }}>
      <h2 style={{ marginBottom: "1.5rem" }}>🛒 Your Cart</h2>

      <div style={{ marginBottom: "1rem", display: "flex", gap: "1rem" }}>
        <button onClick={() => navigate("/coffee")}>Back Home</button>
        <button onClick={handleGoToHistory}>View Order History</button>
      </div>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {allItems.map((item) => {
          const key = `${item.category}-${item.id}`;

          return (
            <li
              key={key}
              style={{
                position: "relative",
                marginBottom: "1rem",
                borderRadius: "8px",
                border: "1px solid #ccc",
                backgroundColor: "#fefefe",
                padding: "1rem",
                display: "flex",
                flexDirection: "column",
                cursor: "pointer",
              }}
              // If you want to add swipe toggle or click handlers, add here
            >
              {/* Remove button */}
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  removeFromCart(item.id, item.category);
                }}
                style={{
                  position: "absolute",
                  left: 0,
                  top: 0,
                  height: "100%",
                  width: "50px",
                  backgroundColor: "red",
                  color: "white",
                  border: "none",
                  borderRadius: "8px 0 0 8px",
                  cursor: "pointer",
                  fontSize: "1.5rem",
                }}
                title="Remove item"
              >
                🗑️
              </button>

              <label
                style={{
                  fontWeight: "bold",
                  marginBottom: "0.5rem",
                  marginLeft: "60px", // to avoid overlapping remove button
                  cursor: "pointer",
                  display: "flex",
                  alignItems: "center",
                }}
              >
                <input
                  type="checkbox"
                  checked={selectedItems.has(key)}
                  onChange={(e) => {
                    e.stopPropagation();
                    toggleItem(key);
                  }}
                  style={{ marginRight: "10px" }}
                />
                {item.name}
              </label>

              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  marginLeft: "60px",
                }}
              >
                <div style={{ fontWeight: "normal" }}>${item.price.toFixed(2)}</div>

                <input
                  type="number"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => {
                    e.stopPropagation();
                    handleQuantityChange(item, e.target.value);
                  }}
                  style={{ width: "60px" }}
                />
              </div>
            </li>
          );
        })}
      </ul>

      <div
        style={{
          fontWeight: "bold",
          fontSize: "1.2rem",
          textAlign: "left",
          marginTop: "2rem",
        }}
      >
        Total: ${total.toFixed(2)}
      </div>

      <button
        onClick={handleOrderSelected}
        style={{
          marginTop: "1rem",
          padding: "10px 20px",
          backgroundColor: "#28a745",
          color: "white",
          fontWeight: "bold",
          border: "none",
          borderRadius: "6px",
          cursor: "pointer",
        }}
      >
        Order Selected Items
      </button>
    </div>
  );
}
