import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

import AddToCart from "./AddToCart";
import CartIcon from "./CartIcon"; // Your cart icon with badge

export default function CoffeeShop() {
  const [cart, setCart] = useState({
    espresso: [
      // sample items for demo, replace with your real data
      { id: 1, name: "Espresso Small", price: 2.5, quantity: 2 },
    ],
    pastry: [],
    tea: [],
    refresher: [],
    blended: [],
  });

  const [cartOpen, setCartOpen] = useState(false);

  const removeFromCart = (id, category) => {
    setCart((prev) => ({
      ...prev,
      [category]: prev[category].filter((item) => item.id !== id),
    }));
  };

  const updateQuantity = (id, category, quantity) => {
    setCart((prev) => ({
      ...prev,
      [category]: prev[category].map((item) =>
        item.id === id ? { ...item, quantity } : item
      ),
    }));
  };

  // Calculate total cart items
  const totalItems = Object.values(cart).reduce(
    (total, items) =>
      total + items.reduce((sum, item) => sum + item.quantity, 0),
    0
  );

  return (
    <div>
      <h1>Welcome to CoffeeShop</h1>

      {/* Your product groups here */}
      {/* ... */}

      {/* Cart Icon in bottom right */}
      <div
        onClick={() => setCartOpen((open) => !open)}
        style={{ position: "fixed", bottom: "20px", right: "20px", cursor: "pointer" }}
      >
        <CartIcon totalItems={totalItems} />
      </div>

      {/* Render AddToCart modal */}
      {cartOpen && (
        <div
          style={{
            position: "fixed",
            inset: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
          onClick={() => setCartOpen(false)}
        >
          <div
            style={{ backgroundColor: "white", padding: "1rem", borderRadius: 8, width: "90%", maxWidth: 600, maxHeight: "80vh", overflowY: "auto" }}
            onClick={(e) => e.stopPropagation()}
          >
            <AddToCart
              cart={cart}
              removeFromCart={removeFromCart}
              updateQuantity={updateQuantity}
            />
            <button onClick={() => setCartOpen(false)} style={{ marginTop: "1rem" }}>
              Close Cart
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
