import React from "react";
import mocha from "./assets/tea1.png";
import teaImg from "./assets/teacoffee.png";
import refresher from "./assets/refresher.png";
const espressoProducts = [
  { id: 1, name: "Green Tea", price:129.99, image: refresher },
  { id: 2, name: "Jasmine Tea", price: 130.50 },
  { id: 3, name: "Yellow Tea", price: 220.90, image: mocha },
  { id: 4, name: "Black Tea", price: 159.00, image: teaImg },
];

export default function Tea({ addToCart }) {
  return (
    <div>
      <h1>Tea Menu</h1>
      <ul style={{
        display: "flex",
        justifyContent: "space-around"
        }}>
        {espressoProducts.map((product) => (
          <li key={product.id} style={{ 
            marginBottom: "1rem",
            background: "orangered",
            padding: "10px",
            height: "180px",
            width: "200px",
            boxShadow: "0 4px 4px brown",
            borderRadius: "10px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            color: "wheat"
            }}>
            
            <button 
             style ={{
                borderRadius: "50%",
                padding:"5px",
                border: "2px solid white",
                boxShadow: "0 0 2px black",
                background: "white",
                width: "35px",
              }}
              onClick={() => addToCart({ ...product, quantity: 1 }, "espresso")}
            >
              ðŸ›’
            </button>
            <div style={{ }} >
              ${product.price.toFixed(2)}
            </div>
            <img
              src={product.image}
              alt={product.name}
              style={{ marginTop: "130px",position: "absolute",width: "150px", height: "auto", borderRadius: "10px" }}
            />
  
            
            <div style={{
             fontFamily: "Sansita Swashed,cursive",
             fontWeight: "bold",
             position: "absolute",
             marginTop: "330px",
             color: "#603417"
              }}>
              {product.name}
          </div>
          
          </li>
        ))}
      </ul>
    </div>
  );
}
