import React, { useState } from "react";
import AddToCart from "./AddToCart";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";

const productGroups = [
  { name: "Espresso", items: [{ id: 1, name: "Espresso Shot", price: 3.0 }] },
  { name: "Latte", items: [{ id: 2, name: "Vanilla Latte", price: 4.5 }] },
  { name: "Cappuccino", items: [{ id: 3, name: "Cappuccino Classic", price: 4.0 }] },
  { name: "Pastry", items: [{ id: 4, name: "Croissant", price: 2.5 }] },
  { name: "Tea", items: [{ id: 5, name: "Chamomile Tea", price: 3.5 }] },
];

export default function CoffeeShop({
  addToCart,
  totalItems,
  cart,
  removeFromCart,
  updateQuantity,
}) {
  const [showCartModal, setShowCartModal] = useState(false);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>☕ Welcome to CoffeeShop</h1>

      {productGroups.map((group) => (
        <div key={group.name} style={{ margin: "1rem 0" }}>
          <h2>{group.name}</h2>
          {group.items.map((item) => (
            <div
              key={item.id}
              style={{ display: "flex", justifyContent: "space-between" }}
            >
              <span>
                {item.name} - ${item.price.toFixed(2)}
              </span>
              <button onClick={() => addToCart(item, group.name.toLowerCase())}>
                Add to Cart
              </button>
            </div>
          ))}
        </div>
      ))}

      {/* Cart Icon with badge */}
      <button
        onClick={() => setShowCartModal(true)}
        style={{
          position: "fixed",
          top: "1rem",
          right: "1rem",
          background: "none",
          border: "none",
          cursor: "pointer",
          zIndex: 1001,
          fontSize: "1.5rem",
          color: "black",
        }}
        aria-label="Open Cart"
      >
        <FontAwesomeIcon icon={faShoppingCart} />
        {totalItems > 0 && (
          <span
            style={{
              position: "absolute",
              top: "0",
              right: "0",
              background: "red",
              borderRadius: "50%",
              color: "white",
              padding: "2px 6px",
              fontSize: "0.75rem",
            }}
          >
            {totalItems}
          </span>
        )}
      </button>

      {/* Modal */}
      {showCartModal && (
        <div
          onClick={() => setShowCartModal(false)}
          style={{
            position: "fixed",
            inset: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            zIndex: 1000,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div
            onClick={(e) => e.stopPropagation()}
            style={{
              backgroundColor: "white",
              borderRadius: "10px",
              padding: "1rem",
              width: "90%",
              maxWidth: "700px",
              maxHeight: "80vh",
              overflowY: "auto",
              position: "relative",
            }}
          >
            <button
              onClick={() => setShowCartModal(false)}
              style={{
                position: "absolute",
                top: "10px",
                right: "20px",
                fontSize: "1.2rem",
                background: "none",
                border: "none",
                cursor: "pointer",
              }}
            >
              ✖
            </button>

            <AddToCart
              cart={cart}
              removeFromCart={removeFromCart}
              updateQuantity={updateQuantity}
            />
          </div>
        </div>
      )}
    </div>
  );
}
