import React from "react";
import mocha from "./assets/cheesepuff.png";
import latte from "./assets/cream puff.png";
import honey from "./assets/honey.png";
import strawberry from "./assets/strawberrypastry.png"
import { useNavigate } from "react-router-dom";

const espressoProducts = [
  { id: 1, name: "Caffee Macchiato", price:129.99, image: strawberry },
  { id: 2, name: "Capuccino", price: 130.50, image: honey },
  { id: 3, name: "Mocha", price: 220.90, image: mocha },
  { id: 4, name: "Latte", price: 159.00, image: latte },
];

export default function Pastry({ addToCart }) {
  const navigate = useNavigate();
  return (
    <div >
      <h1 onClick={() => navigate("/coffee")}
        style={{ cursor: "pointer", color: "#8B4513", textDecoration: "underline" }}>Pastry Menu</h1>
      <ul style={{
        display: "flex",
        justifyContent: "space-around"
        }}>
        {espressoProducts.map((product) => (
          <li key={product.id} style={{ 
            marginBottom: "1rem",
            background: "orangered",
            padding: "10px",
            height: "180px",
            width: "200px",
            boxShadow: "0 4px 4px brown",
            borderRadius: "10px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            color: "wheat"
            }}>
            
            <button 
             style ={{
                borderRadius: "50%",
                padding:"5px",
                border: "2px solid white",
                boxShadow: "0 0 2px black",
                background: "white",
                width: "35px",

              }}
              onClick={() => addToCart({ ...product, quantity: 1 }, "espresso")}
            >
              ðŸ›’
            </button>
            <div style={{ }} >
              ${product.price.toFixed(2)}
            </div>
            <img
              src={product.image}
              alt={product.name}
              style={{ marginTop: "180px",position: "absolute",width: "150px", height: "auto", borderRadius: "10px" }}
            />
  
            
            <div style={{
             fontFamily: "Sansita Swashed,cursive",
             fontWeight: "bold",
             position: "absolute",
             marginTop: "300px",
             color: "#603417"
              }}>
              {product.name}
          </div>
          
          </li>
        ))}
      </ul>
    </div>
  );
}
