const handleOrderSelected = () => {
  if (selectedArray.length === 0) {
    alert("Please select at least one item to order.");
    return;
  }

  // Load existing history from localStorage
  const existingHistory = JSON.parse(localStorage.getItem("orderHistory") || "[]");

  // Add current order to history
  const newHistory = [...existingHistory, selectedArray];

  // Save updated history
  localStorage.setItem("orderHistory", JSON.stringify(newHistory));

  // Navigate with history (optional)
  navigate("/payment", { state: { selectedItems: selectedArray } });

  selectedArray.forEach((item) => {
    removeFromCart(item.id, item.category);
  });
};
