import React from "react";
import main from "./assets/main.jpg";
import coffeeLogo from "./assets/jasmine.png";
import CircularText from "./CircularText";
import SimpleNavbar from "./SimpleNavbar";
import { Link } from "react-router-dom";

export default function Home() {
  const btnStyle = {
    padding: "10px",
    background: "#947848",
    color: "wheat",
    borderRadius: "5px",
    width: "200px",
    border: "2px solid transparent",
    fontFamily: "Arial"
  };

  const radius = 110;

  return (
    <div style={{ display: "flex", padding: "1rem", fontFamily: "sans-serif" }}>
      {/* Left image */}
      <img
        src={main}
        alt="Coffee Shop Main"
        style={{
          width: "50%",
          height: "auto",
          borderRadius: "10px"
        }}
      />

      {/* Right content */}
      <div style={{ width: "50%", textAlign: "center", padding: "1rem" }}>
        <SimpleNavbar />

        {/* Circular text and logo */}
        <div style={{ position: "relative", width: radius * 2, height: radius * 2, margin: "0 auto" }}>
          {/* Top circular text (no rotation, curved only) */}
          <CircularText
            text="The Daily Grind"
            radius={radius}
            startAngle={180}
            endAngle={0}
            invert={false}
            rotateLetters={false}
          />

          {/* Center logo */}
          <img
            src={coffeeLogo}
            alt="Coffee Logo"
            style={{
              position: "absolute",
              top: "50%",
              left: "50%",
              width: "100px",
              height: "100px",
              transform: "translate(-50%, -50%)",
              borderRadius: "50%"
            }}
          />

          {/* Bottom circular text (same style as top, just below) */}
          <CircularText
            text="Coffee House"
            radius={radius}
            startAngle={180}
            endAngle={360}
            invert={true}
            rotateLetters={false}
          />
        </div>

        {/* Order Now */}
        <p style={{ marginTop: "2rem", fontSize: "14px" }}>
          When placing an order, select the option<br />
          <b>"Contactless delivery"</b> and the courier will leave your order at the door.
        </p>
        <Link to="/coffee">
          <button style={btnStyle}>ORDER NOW</button>
        </Link>
      </div>
    </div>
  );
}
