import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";

export default function CoffeeShop() {
  const navigate = useNavigate();
  const location = useLocation();

  const buttonStyle = {
    border: "none",
    cursor: "pointer",
    background: "none",
    textAlign: "center",
  };

  const imgStyle = {
    width: "120px",
    height: "150px",
    borderRadius: "10px",
    objectFit: "cover",
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Choose Your Coffee</h1>

      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(3, 1fr)",
          gridTemplateRows: "auto auto",
          gap: "1.5rem",
          maxWidth: "600px",
          margin: "2rem auto",
          position: "relative",
        }}
      >
        {/* Top row: 3 buttons */}
        <button onClick={() => navigate("/espresso")} style={buttonStyle}>
          <img src={espressoImg} alt="Espresso" style={imgStyle} />
          <div>Espresso</div>
        </button>

        <button onClick={() => navigate("/tea")} style={buttonStyle}>
          <img src={teaImg} alt="Tea" style={imgStyle} />
          <div>Tea</div>
        </button>

        <button onClick={() => navigate("/blended")} style={buttonStyle}>
          <img src={blendedImg} alt="Blended" style={imgStyle} />
          <div>Blended Beverage</div>
        </button>

        {/* Bottom row: 2 buttons placed between top ones */}
        <div style={{ gridColumn: "1 / 3", justifySelf: "center" }}>
          <button onClick={() => navigate("/refresher")} style={buttonStyle}>
            <img src={refresherImg} alt="Refresher" style={imgStyle} />
            <div>Refresher</div>
          </button>
        </div>

        <div style={{ gridColumn: "2 / 4", justifySelf: "center" }}>
          <button onClick={() => navigate("/pastry")} style={buttonStyle}>
            <img src={pastryImg} alt="Pastry" style={imgStyle} />
            <div>Pastry</div>
          </button>
        </div>
      </div>
    </div>
  );
}
