import React from "react";

function CircularText({ text, radius = 120 }) {
  const characters = text.split("");
  const totalAngle = 180; // half circle from left (180째) to right (0째)
  const degreeStep = totalAngle / (characters.length - 1);

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        // Angle from 180 (left center) to 0 (right center)
        const angle = 180 - index * degreeStep; 
        const rad = (angle * Math.PI) / 180;

        const x = radius + radius * Math.cos(rad);
        const y = radius + radius * Math.sin(rad);

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${angle}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}

export default function TextWithBottomRight() {
  const radius = 120;

  return (
    <div style={{ position: "relative", width: radius * 2, height: radius * 2 + 40 }}>
      {/* Curved text from left center (9 o'clock) to right center (3 o'clock) */}
      <CircularText text="the daily grind" radius={radius} />

      {/* Bottom right straight text */}
      <div
        style={{
          position: "absolute",
          bottom: 0,
          right: 0,
          fontSize: 20,
          fontFamily: "Georgia, serif",
          color: "#5a3e36",
          userSelect: "none",
        }}
      >
        coffee house
      </div>
    </div>
  );
}
