import React from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";
import CartIcon from "./CartIcon";
import "./App.css";
export default function CoffeeShop({addToCart, totalItems }) {
  const navigate = useNavigate();

  const buttonStyle = {
    border: "none",
    width: "auto",
    height: "240px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    cursor: "pointer",
    background: "none",
    textAlign: "center",
  };

  const imgStyle = {
    width: "120px",
    height: "auto",
    borderRadius: "10px",
    objectFit: "cover",
  };

  const labelStyle = {
    fontFamily: "Sansita Swashed,cursive",
    marginTop: "10px",
    background: "#A17557",
    borderRadius: "5px",
    padding: "5px",
    width: "120px",
    border: "2px solid black",
  };

  return (
    <div style={{ textAlign: "center" }}>
      
      <CartIcon totalItems={totalItems} />
      <div>
        < button  onClick={() => navigate("/")}>Back Home
        </button>
      </div>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(5, 1fr)",
          gap: "1.5rem",
          maxWidth: "800px",
          margin: "1rem auto",
          background: "none",
        }}
      >
        <div style={{ gridColumn: "1 / 2" }}>
          <button onClick={() => navigate("/espresso")} style={buttonStyle}>
            <img src={espressoImg} alt="Espresso" style={imgStyle} />
            <div style={labelStyle}>Espresso</div>
          </button>
        </div>

        <div style={{ gridColumn: "3 / 4" }}>
          <button onClick={() => navigate("/tea")} style={buttonStyle}>
            <img src={teaImg} alt="Tea" style={imgStyle} />
            <div style={labelStyle}>Tea</div>
          </button>
        </div>

        <div style={{ gridColumn: "5 / 6" }}>
          <button onClick={() => navigate("/blended")} style={buttonStyle}>
            <img src={blendedImg} alt="Blended" style={imgStyle} />
            <div style={labelStyle}>Blended Beverage</div>
          </button>
        </div>

        <div style={{ gridColumn: "2 / 3", justifySelf: "center" }}>
          <button onClick={() => navigate("/refresher")} style={buttonStyle}>
            <img src={refresherImg} alt="Refresher" style={imgStyle} />
            <div style={labelStyle}>Refresher</div>
          </button>
        </div>

        <div style={{ gridColumn: "4 / 5", justifySelf: "center" }}>
          <button onClick={() => navigate("/pastry")} style={buttonStyle}>
            <img src={pastryImg} alt="Pastry" style={imgStyle} />
            <div style={labelStyle}>Pastry</div>
          </button>
        </div>
      </div>
    </div>
  );
}
