import React, { useState } from "react";

const coffeeMenu = [
  { id: 1, name: "Espresso", price: 3 },
  { id: 2, name: "Cappuccino", price: 4 },
  { id: 3, name: "Latte", price: 4.5 },
  { id: 4, name: "Mocha", price: 5 },
];

export default function CoffeeShop() {
  const [cart, setCart] = useState([]);

  const addToCart = (coffee) => {
    setCart((prevCart) => {
      const itemInCart = prevCart.find((item) => item.id === coffee.id);
      if (itemInCart) {
        return prevCart.map((item) =>
          item.id === coffee.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...coffee, quantity: 1 }];
      }
    });
  };

  const removeFromCart = (id) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== id));
  };

  const totalPrice = cart.reduce(
    (total, item) => total + item.price * item.quantity,
    0
  );

  return (
    <div>
      <h1>Coffee Menu</h1>
      <ul>
        {coffeeMenu.map((coffee) => (
          <li key={coffee.id}>
            {coffee.name} - ${coffee.price.toFixed(2)}{" "}
            <button onClick={() => addToCart(coffee)}>Add to Cart</button>
          </li>
        ))}
      </ul>

      <h2>Your Cart</h2>
      {cart.length === 0 ? (
        <p>Cart is empty.</p>
      ) : (
        <ul>
          {cart.map((item) => (
            <li key={item.id}>
              {item.name} x {item.quantity} = ${(item.price * item.quantity).toFixed(2)}{" "}
              <button onClick={() => removeFromCart(item.id)}>Remove</button>
            </li>
          ))}
        </ul>
      )}

      <h3>Total: ${totalPrice.toFixed(2)}</h3>
    </div>
  );
}
