import React from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refresher.png";

export default function CoffeeShop({ cart, setCart }) {
  const navigate = useNavigate();

  // addToCart function moved here, uses setCart from props
  const addToCart = (item, category) => {
    setCart((prev) => {
      const categoryItems = prev[category] || [];
      const exists = categoryItems.find((i) => i.id === item.id);

      let updatedCategory;
      if (exists) {
        updatedCategory = categoryItems.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        updatedCategory = [...categoryItems, { ...item, quantity: 1 }];
      }

      return { ...prev, [category]: updatedCategory };
    });
  };

  // Example product items
  const espressoSample = { id: "espresso1", name: "Espresso", price: 3 };
  const refresherSample = { id: "refresher1", name: "Refresher", price: 4 };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Choose Your Coffee</h1>
      <div style={{ display: "flex", justifyContent: "center", gap: "2rem" }}>
        <div>
          <img
            src={espressoImg}
            alt="Espresso"
            style={{ width: "150px", height: "150px", borderRadius: "10px", cursor: "pointer" }}
            onClick={() => navigate("/espresso")}
          />
          <div>Espresso</div>
          <button onClick={() => addToCart(espressoSample, "espresso")}>
            Add to Cart
          </button>
        </div>

        <div>
          <img
            src={refresherImg}
            alt="Refresher"
            style={{ width: "150px", height: "150px", borderRadius: "10px", cursor: "pointer" }}
            onClick={() => navigate("/refresher")}
          />
          <div>Refresher</div>
          <button onClick={() => addToCart(refresherSample, "refresher")}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}
