import React from "react";
import SimpleNavbar from "./SimpleNavbar";

export default function About() {
  const containerStyle = {
    backgroundColor: "#fff7f0",
    minHeight: "100vh",
    padding: "3rem 1rem",
    fontFamily: "'Roboto', sans-serif",
    color: "#6f4e37",
    display: "flex",
    justifyContent: "center",
  };

  const labelStyle = {
    display: "flex",
    flexDirection: "column",
    gap: "2rem",
    maxWidth: "600px",
  };

  const card = {
    background: "#60371C",
    color: "wheat",
    padding: "30px 25px",
    fontSize: "1.25rem",
    borderRadius: "30px",
    border: "6px solid brown",
    boxShadow: "0 0 10px inset black",
    fontFamily: "'Akaya Telivigala', cursive",
    lineHeight: "1.5",
    textAlign: "center",
  };

  return (
    <div style={containerStyle}>
      <div style={{ width: "100%", maxWidth: "720px" }}>
        <SimpleNavbar />
        <div style={labelStyle}>
          <p style={card}>
            At Daily Grind Coffee House, we believe that every cup of coffee tells a story.
            Nestled in the heart of Bongabong, we're more than just a coffee shopâ€”we're a place where
            early risers, late-night thinkers, and weekend wanderers come together over
            expertly brewed coffee and handcrafted treats.
          </p>
          <p style={card}>
            Our passion for rich flavors and high quality beans drives us to serve the best espresso,
            lattes, and cold brews in town. Whether you're here for a quick caffeine fix or a cozy spot
            to work and unwind, we've got you covered with our warm ambiance and friendly baristas.
          </p>
        </div>
      </div>
    </div>
  );
}
