import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";
import CartIcon from "./CartIcon";
import CartModal from "./CartModal";
import "./App.css";

export default function CoffeeShop({ totalItems }) {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [cartOpen, setCartOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const toggleMenu = () => setMenuOpen((open) => !open);
  const toggleCart = () => setCartOpen((open) => !open);

  const buttonStyle = {
    backgroundColor: "#fff",
    border: "1px solid #ccc",
    borderRadius: "12px",
    padding: "10px",
    cursor: "pointer",
    width: "140px",
    boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
    transition: "transform 0.2s",
  };

  const imgStyle = {
    width: "100px",
    height: "100px",
    objectFit: "cover",
    marginBottom: "0.5rem",
  };

  const labelStyle = {
    fontSize: "16px",
    fontWeight: "bold",
    color: "#5a3e1b",
  };

  const floatingMenuStyle = {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    zIndex: 1000,
  };

  const hiddenStyle = {
    opacity: 0,
    transform: "translateY(10px)",
    pointerEvents: "none",
    transition: "opacity 0.3s, transform 0.3s",
  };

  const visibleStyle = {
    opacity: 1,
    transform: "translateY(0)",
    transition: "opacity 0.3s, transform 0.3s",
  };

  const menuButtonStyle = {
    backgroundColor: "#5a3e1b",
    color: "#fff",
    border: "none",
    borderRadius: "50%",
    width: "60px",
    height: "60px",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontSize: "24px",
    cursor: "pointer",
    boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
  };

  const products = [
    { key: "espresso", label: "Espresso", img: espressoImg },
    { key: "refresher", label: "Refresher", img: refresherImg },
    { key: "tea", label: "Tea", img: teaImg },
    { key: "pastry", label: "Pastry", img: pastryImg },
    { key: "blended", label: "Blended", img: blendedImg },
  ];

  return (
    <div style={{ textAlign: "center", paddingBottom: "100px" }}>
      <h2 style={{ color: "#5a3e1b" }}>Our Coffee Menu</h2>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(160px, 1fr))",
          gap: "2rem",
          padding: "1rem",
          maxWidth: "900px",
          margin: "0 auto",
        }}
      >
        {products.map(({ key, label, img }) => (
          <div key={key} style={{ textAlign: "center" }}>
            <button
              onClick={() => navigate(`/${key}`)}
              style={buttonStyle}
              aria-label={`View ${label}`}
              title={`View ${label}`}
            >
              <img src={img} alt={label} style={imgStyle} />
              <div style={labelStyle}>{label}</div>
            </button>
          </div>
        ))}
      </div>

      {/* Floating menu */}
      <div style={floatingMenuStyle}>
        {/* Back button */}
        <button
          onClick={() => {
            navigate("/");
            setMenuOpen(false);
            setCartOpen(false);
            setSelectedProduct(null);
          }}
          aria-label="Back to Home"
          title="Back to Home"
          style={{
            ...menuButtonStyle,
            backgroundColor: "#eee",
            color: "#5a3e1b",
            ...(menuOpen ? visibleStyle : hiddenStyle),
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            viewBox="0 0 24 24"
          >
            <polyline points="15 18 9 12 15 6" />
          </svg>
        </button>

        {/* Cart Icon */}
        <div
          onClick={toggleCart}
          style={menuOpen ? visibleStyle : hiddenStyle}
          aria-label="Open Cart"
          title="Open Cart"
          role="button"
          tabIndex={0}
          onKeyDown={(e) => {
            if (e.key === "Enter" || e.key === " ") {
              e.preventDefault();
              toggleCart();
            }
          }}
        >
          <CartIcon totalItems={totalItems} />
        </div>

        {/* Main Menu Toggle Button */}
        <button
          onClick={toggleMenu}
          aria-label={menuOpen ? "Close menu" : "Open menu"}
          title={menuOpen ? "Close menu" : "Open menu"}
          style={menuButtonStyle}
        >
          {menuOpen ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
            >
              <line x1="18" y1="6" x2="6" y2="18" />
              <line x1="6" y1="6" x2="18" y2="18" />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
            >
              <line x1="3" y1="12" x2="21" y2="12" />
              <line x1="3" y1="6" x2="21" y2="6" />
              <line x1="3" y1="18" x2="21" y2="18" />
            </svg>
          )}
        </button>
      </div>

      {/* Cart Modal */}
      {cartOpen && (
        <CartModal
          totalItems={totalItems}
          onClose={() => {
            setCartOpen(false);
            setSelectedProduct(null);
          }}
          selectedProduct={selectedProduct}
        />
      )}
    </div>
  );
}
