import React from "react";

const teaProducts = [
  { id: 5, name: "Green Tea", price: 2.5 },
  { id: 6, name: "Black Tea", price: 2.5 },
  // add more teas here
];

export default function Tea({ addToCart }) {
  return (
    <div>
      <h1>Tea Menu</h1>
      <ul>
        {teaProducts.map((product) => (
          <li key={product.id} style={{ marginBottom: "1rem" }}>
            {product.name} - ${product.price.toFixed(2)}{" "}
            <button onClick={() => addToCart({ ...product, quantity: 1 }, "tea")}>Add to Cart</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
