import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart, addOrderToHistory, clearCart }) {
  const navigate = useNavigate();

  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items)
      ? items.map((item) => ({ ...item, category }))
      : []
  );

  const [selectedItems, setSelectedItems] = useState(
    () => new Set(allItems.map(item => `${item.category}-${item.id}`))
  );

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(key)) {
        newSet.delete(key);
      } else {
        newSet.add(key);
      }
      return newSet;
    });
  };

  const selectedArray = allItems.filter(
    (item) => selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

const handleOrderSelected = () => {
  if (selectedArray.length === 0) {
    alert("Please select at least one item to order.");
    return;
  }

  // You can later let the user choose paymentMethod dynamically
  addOrderToHistory(selectedArray, "Pay on Delivery");

  clearCart();

  alert("Order placed successfully!");
  navigate("/history");
};


  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ marginBottom: "1.5rem" }}>ðŸ›’ Your Cart</h2>

      {allItems.length === 0 ? (
        <p>Your cart is currently empty.</p>
      ) : (
        <>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {allItems.map((item) => {
              const key = `${item.category}-${item.id}`;
              return (
                <li
                  key={key}
                  style={{
                    marginBottom: "1rem",
                    padding: "1rem",
                    border: "1px solid #ccc",
                    borderRadius: "8px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "space-between",
                    backgroundColor: "#fefefe",
                  }}
                >
                  <label style={{ flexGrow: 1, cursor: "pointer" }}>
                    <input
                      type="checkbox"
                      checked={selectedItems.has(key)}
                      onChange={() => toggleItem(key)}
                      style={{ marginRight: "10px" }}
                    />
                    <strong>{item.name}</strong> â€” {item.quantity} Ã— ${item.price.toFixed(2)} = <strong>${(item.price * item.quantity).toFixed(2)}</strong>
                  </label>

                  <button
                    style={{
                      padding: "6px 12px",
                      backgroundColor: "#ff4d4f",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      cursor: "pointer",
                    }}
                    onClick={() => removeFromCart(item.id, item.category)}
                  >
                    Remove
                  </button>
                </li>
              );
            })}
          </ul>

          <h3>Total: ${total.toFixed(2)}</h3>

          <button
            onClick={handleOrderSelected}
            style={{
              marginTop: "1rem",
              padding: "10px 20px",
              backgroundColor: "#28a745",
              color: "white",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Order Selected Items
          </button>
        </>
      )}
    </div>
  );
}
