import React from "react";

export default function History({ orderHistory, addMultipleToCart }) {
  if (orderHistory.length === 0) {
    return <p>You have no past orders.</p>;
  }

  return (
    <div>
      <h1>Order History</h1>
      {orderHistory.map((order) => (
        <div
          key={order.id}
          style={{ border: "1px solid #ccc", margin: "1rem 0", padding: "1rem" }}
        >
          <p>
            <strong>Order ID:</strong> {order.id}
          </p>
          <p>
            <strong>Date:</strong> {order.date}
          </p>
          <p>
            <strong>Payment Method:</strong>{" "}
            {order.paymentMethod === "gcash" ? "Gcash" : "Pay on Delivery"}
          </p>
          <p>
            <strong>Items:</strong>
          </p>
          <ul>
            {order.items.map((item) => (
              <li key={item.id}>
                {item.name} x {item.quantity} (${(item.price * item.quantity).toFixed(2)})
              </li>
            ))}
          </ul>
          <button onClick={() => addMultipleToCart(order.items)}>
            Order Again
          </button>
        </div>
      ))}
    </div>
  );
}
