import React from "react";
import mocha from "./assets/strawberry2.png";
import mogito from "./assets/mogito.png";
import cocktail from "./assets/cocktail.png";
import green from "./assets/green.png";
const refresherProducts = [
  { id: 1, name: "Caffee Macchiato", price:129.99, image: cocktail},
  { id: 2, name: "Mogito", price: 130.50, image: mogito},
  { id: 3, name: "Strawberry", price: 220.90, image: mocha },
  { id: 4, name: "Latte", price: 159.00, image: green },
];

export default function Refresher({ addToCart }) {
  const navigate = useNavigate();
  return (
    <div>
      <h1 onClick={() => navigate("/coffee")}
        style={{ cursor: "pointer", color: "#8B4513", textDecoration: "underline" }}>Refresher Menu</h1>
      <ul style={{
        display: "flex",
        justifyContent: "space-around"
        }}>
        {refresherProducts.map((product) => (
          <li key={product.id} style={{ 
            marginBottom: "1rem",
            background: "orangered",
            padding: "10px",
            height: "180px",
            width: "200px",
            boxShadow: "0 4px 4px brown",
            borderRadius: "10px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            color: "wheat"
            }}>
            
            <button 
            style ={{
              borderRadius: "50%",
              padding:"5px",
              border: "2px solid white",
              boxShadow: "0 0 2px black",
              background: "white",
              width: "35px",
             
              }}
              onClick={() => addToCart({ ...product, quantity: 1 }, "espresso")}
            
            >
              ðŸ›’
            </button>
            <div style={{ }} >
              ${product.price.toFixed(2)}
            </div>
            <img
              src={product.image}
              alt={product.name}
              style={{ marginTop: "220px",position: "absolute",width: "95px", height: "auto", borderRadius: "10px" }}
            />
            
            <div style={{
             fontFamily: "Sansita Swashed,cursive",
             fontWeight: "bold",
             position: "absolute",
             marginTop: "410px",
             color: "#603417"
              }}>
              {product.name}
          </div>
          
          </li>
        ))}
      </ul>
    </div>
  );
}
