import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";

export default function Payment({ addOrderToHistory, clearCart }) {
  const location = useLocation();
  const navigate = useNavigate();

  // Get selectedItems passed via navigation state, default to empty array if none
  const selectedItems = location.state?.selectedItems || [];

  const [paymentMethod, setPaymentMethod] = useState("gcash");
  const [confirmed, setConfirmed] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (selectedItems.length === 0) {
      alert("No items to order.");
      navigate("/cart");
      return;
    }

    // Create order object with selected items and payment method
    const order = {
      id: Date.now(),
      items: selectedItems,
      paymentMethod,
      date: new Date().toLocaleString(),
    };

    addOrderToHistory(order);
    clearCart();
    setConfirmed(true);
  };

  return (
    <div>
      <h1>Payment Options</h1>

      {confirmed ? (
        <div>
          <h2>Order Confirmed!</h2>
          <p>You chose: {paymentMethod === "gcash" ? "Gcash" : "Pay on Delivery"}</p>
          <p>Thank you for your order â˜•</p>
        </div>
      ) : (
        <>
          {selectedItems.length === 0 ? (
            <p>No items selected. Please add items to your cart first.</p>
          ) : (
            <>
              <h3>Your Order:</h3>
              <ul>
                {selectedItems.map((item) => (
                  <li key={item.id}>
                    {item.name} x {item.quantity} (${(item.price * item.quantity).toFixed(2)})
                  </li>
                ))}
              </ul>
              <h3>
                Total: $
                {selectedItems
                  .reduce((sum, item) => sum + item.price * item.quantity, 0)
                  .toFixed(2)}
              </h3>

              <form onSubmit={handleSubmit}>
                <label>
                  <input
                    type="radio"
                    value="gcash"
                    checked={paymentMethod === "gcash"}
                    onChange={() => setPaymentMethod("gcash")}
                  />
                  Gcash
                </label>
                <br />
                <label>
                  <input
                    type="radio"
                    value="cod"
                    checked={paymentMethod === "cod"}
                    onChange={() => setPaymentMethod("cod")}
                  />
                  Pay on Delivery
                </label>
                <br />
                <button type="submit">Confirm Order</button>
              </form>
            </>
          )}
        </>
      )}
    </div>
  );
}
