import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart, updateQuantity }) {
  const navigate = useNavigate();

  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items) ? items.map((item) => ({ ...item, category })) : []
  );

  // ‚ùó Start with no items selected
  const [selectedItems, setSelectedItems] = useState(new Set());

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      newSet.has(key) ? newSet.delete(key) : newSet.add(key);
      return newSet;
    });
  };

  const handleQuantityChange = (item, newQty) => {
    const quantity = parseInt(newQty, 10);
    if (!isNaN(quantity) && quantity > 0) {
      updateQuantity(item.id, item.category, quantity);
    }
  };

  const selectedArray = allItems.filter(item =>
    selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    // Go to payment page with selected items
    navigate("/payment", { state: { selectedItems: selectedArray } });

    // Only remove selected items from cart
    selectedArray.forEach(item => {
      removeFromCart(item.id, item.category);
    });
  };

  const handleGoToHistory = () => {
    navigate("/history");
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial, sans-serif" }}>
      <h2 style={{ marginBottom: "1.5rem" }}>üõí Your Cart</h2>
      <div>
        <button onClick={() => navigate("/coffee")}>Back Home</button>
      </div>
      <button onClick={handleGoToHistory}>View Order History</button>
      {allItems.length === 0 ? (
        <p>Your cart is currently empty.</p>
      ) : (
        <>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {allItems.map((item) => {
  const key = `${item.category}-${item.id}`;
  const [isSwiped, setIsSwiped] = useState(false); // move this into a separate component if needed

  return (
    <div className="cart-item-container" key={key}>
      {/* Red Delete Background */}
      <div className="swipe-background">
        üóëÔ∏è
      </div>

      {/* Actual Item */}
      <li
        className="cart-item"
        style={{
          transform: isSwiped ? "translateX(-200px)" : "translateX(0)",
        }}
        onClick={() => setIsSwiped(!isSwiped)} // toggle swipe for demo
      >
        <label style={{ flexGrow: 1, cursor: "pointer" }}>
          <input
            type="checkbox"
            checked={selectedItems.has(key)}
            onChange={() => toggleItem(key)}
            style={{ marginRight: "10px" }}
          />
          <strong>{item.name}</strong>
          {" ‚Äî "}
          <input
            type="number"
            min="1"
            value={item.quantity}
            onChange={(e) => handleQuantityChange(item, e.target.value)}
            style={{ width: "60px", margin: "0 10px" }}
          />
          √ó ${item.price.toFixed(2)} ={" "}
          <strong>${(item.price * item.quantity).toFixed(2)}</strong>
        </label>

        <button
          style={{
            padding: "6px 12px",
            backgroundColor: "#ff4d4f",
            color: "white",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
          onClick={() => removeFromCart(item.id, item.category)}
        >
          Remove
        </button>
      </li>
    </div>
  );
})}

          </ul>

          <h3>Total: ${total.toFixed(2)}</h3>

          <button
            onClick={handleOrderSelected}
            style={{
              marginTop: "1rem",
              padding: "10px 20px",
              backgroundColor: "#28a745",
              color: "white",
              fontWeight: "bold",
              border: "none",
              borderRadius: "6px",
              cursor: "pointer",
            }}
          >
            Order Selected Items
          </button>
        </>
      )}
    </div>
  );
}
