import React from "react";

function CircularText({ text, radius = 120, startAngle = 180, endAngle = 0 }) {
  const characters = text.split("");
  const totalAngle = Math.abs(endAngle - startAngle);
  const degreeStep = totalAngle / (characters.length - 1);

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        // Angle interpolation from startAngle to endAngle
        const angle =
          startAngle > endAngle
            ? startAngle - index * degreeStep
            : startAngle + index * degreeStep;

        const rad = (angle * Math.PI) / 180;

        // x and y position on circle edge
        const x = radius + radius * Math.cos(rad);

        // For top arc, y is above center; for bottom arc, y is below center
        const y =
          angle > 90
            ? radius - radius * Math.sin(rad) // top half
            : radius - radius * Math.sin(rad); // bottom half (sin is negative so this works too)

        // Rotate letter so it stays upright along the curve
        // For top arc, rotate(angle - 90)
        // For bottom arc, rotate(angle + 90)
        const rotation = angle > 90 ? angle - 90 : angle + 90;

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${rotation}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}

export default function TopAndBottomCurvedText() {
  const radius = 120;

  return (
    <div style={{ position: "relative", width: radius * 2, height: radius * 2 }}>
      {/* Top half circle text */}
      <CircularText text="the daily grind" radius={radius} startAngle={180} endAngle={0} />

      {/* Bottom half circle text */}
      <CircularText text="coffee house" radius={radius} startAngle={360} endAngle={180} />
    </div>
  );
}
