// SimpleNavbar.js
import React from "react";
import { Link, useLocation } from "react-router-dom";

export default function SimpleNavbar() {
  const location = useLocation();

  const isActive = (path) => (location.pathname === path ? { fontWeight: "bold" } : {});

  return (
    <nav style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}>
      <Link to="/" style={isActive("/")}>Home</Link>
      <Link to="/about" style={isActive("/about")}>About</Link>
      <Link to="/service" style={isActive("/service")}>Service</Link>
      <Link to="/contact" style={isActive("/contact")}>Contact</Link>
    </nav>
  );
}
