// CoffeeShop.jsx (Your component updated with Add to Cart buttons)

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";
import CartIcon from "./CartIcon";
import "./App.css";

export default function CoffeeShop({ addToCart, totalItems }) {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => setMenuOpen((open) => !open);

  const buttonStyle = {
    border: "none",
    width: "140px",
    height: "260px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "flex-start",
    cursor: "pointer",
    background: "#f9f6f1",
    borderRadius: "15px",
    boxShadow: "0 4px 8px rgba(0,0,0,0.15)",
    textAlign: "center",
    padding: "15px",
    transition: "transform 0.25s ease, box-shadow 0.25s ease",
  };

  const buttonHoverStyle = {
    transform: "translateY(-6px)",
    boxShadow: "0 8px 16px rgba(0,0,0,0.25)",
  };

  const imgStyle = {
    width: "120px",
    height: "120px",
    borderRadius: "15px",
    objectFit: "cover",
    marginBottom: "12px",
  };

  const labelStyle = {
    fontFamily: "'Sansita Swashed', cursive",
    fontSize: "1.1rem",
    fontWeight: "700",
    color: "#5a3e1b",
    background: "#A17557",
    borderRadius: "8px",
    padding: "8px 10px",
    width: "100%",
    boxSizing: "border-box",
    border: "2px solid #4b3715",
    userSelect: "none",
  };

  const addToCartButtonStyle = {
    marginTop: "auto",
    background: "#4b3715",
    color: "white",
    border: "none",
    borderRadius: "8px",
    padding: "8px 12px",
    cursor: "pointer",
    fontWeight: "600",
  };

  const [hoveredIndex, setHoveredIndex] = React.useState(null);

  const products = [
    { key: "espresso", label: "Espresso", img: espressoImg, area: "espresso" },
    { key: "refresher", label: "Refresher", img: refresherImg, area: "refresher" },
    { key: "tea", label: "Tea", img: teaImg, area: "tea" },
    { key: "pastry", label: "Pastry", img: pastryImg, area: "pastry" },
    { key: "blended", label: "Blended Beverage", img: blendedImg, area: "blended" },
  ];

  const floatingMenuStyle = {
    position: "fixed",
    bottom: "20px",
    right: "20px",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: "15px",
    zIndex: 1000,
    transition: "height 0.3s ease",
  };

  const hiddenStyle = {
    opacity: 0,
    pointerEvents: "none",
    transform: "translateY(20px)",
    transition: "opacity 0.3s ease, transform 0.3s ease",
  };

  const visibleStyle = {
    opacity: 1,
    pointerEvents: "auto",
    transform: "translateY(0)",
  };

  const menuButtonStyle = {
    background: "#A17557",
    border: "none",
    borderRadius: "50%",
    width: "56px",
    height: "56px",
    cursor: "pointer",
    boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "white",
    fontSize: "24px",
    userSelect: "none",
    transition: "background-color 0.3s ease",
  };

  return (
    <div style={{ textAlign: "center", paddingBottom: "80px" }}>
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr 1fr 1fr 1fr",
          gridTemplateRows: "auto auto",
          gridTemplateAreas: `
            ". espresso refresher tea ."
            ". . pastry blended ."
          `,
          maxWidth: "800px",
          margin: "1rem auto",
          gap: "2rem 2rem",
          padding: "0 1rem",
          background: "none",
        }}
      >
        {products.map(({ key, label, img, area }, index) => (
          <div
            key={key}
            style={{
              gridArea: area,
              justifySelf: "center",
              alignSelf: "center",
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              height: "100%",
            }}
          >
            <button
              onClick={() => navigate(`/${key}`)}
              style={{
                ...buttonStyle,
                ...(hoveredIndex === index ? buttonHoverStyle : {}),
                display: "flex",
                flexDirection: "column",
                justifyContent: "flex-start",
                height: "260px",
              }}
              onMouseEnter={() => setHoveredIndex(index)}
              onMouseLeave={() => setHoveredIndex(null)}
              aria-label={label}
              title={label}
            >
              <img src={img} alt={label} style={imgStyle} />
              <div style={labelStyle}>{label}</div>

              {/* Add to Cart button */}
              <button
                style={addToCartButtonStyle}
                onClick={(e) => {
                  e.stopPropagation(); // prevent navigate when clicking this button
                  addToCart(products[index]);
                }}
                aria-label={`Add ${label} to cart`}
                title={`Add ${label} to cart`}
              >
                Add to Cart
              </button>
            </button>
          </div>
        ))}
      </div>

      {/* Floating menu bottom right */}
      <div style={floatingMenuStyle}>
        <button
          onClick={() => {
            navigate("/");
            setMenuOpen(false);
          }}
          aria-label="Back to Home"
          title="Back to Home"
          style={{
            ...menuButtonStyle,
            width: "50px",
            height: "50px",
            background: "#eee",
            color: "#5a3e1b",
            boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
            ...(!menuOpen ? hiddenStyle : visibleStyle),
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            width="24"
            height="24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            strokeLinecap="round"
            strokeLinejoin="round"
            viewBox="0 0 24 24"
          >
            <polyline points="15 18 9 12 15 6" />
          </svg>
        </button>

        <div
          style={{
            ...(!menuOpen ? hiddenStyle : visibleStyle),
            transitionDelay: menuOpen ? "0.1s" : "0s",
          }}
        >
          <CartIcon totalItems={totalItems} />
        </div>

        <button
          onClick={toggleMenu}
          aria-label={menuOpen ? "Close menu" : "Open menu"}
          title={menuOpen ? "Close menu" : "Open menu"}
          style={menuButtonStyle}
        >
          {menuOpen ? (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              fill="none"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
            >
              <line x1="18" y1="6" x2="6" y2="18" />
              <line x1="6" y1="6" x2="18" y2="18" />
            </svg>
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              width="24"
              height="24"
              fill="none"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
              viewBox="0 0 24 24"
            >
              <line x1="3" y1="12" x2="21" y2="12" />
              <line x1="3" y1="6" x2="21" y2="6" />
              <line x1="3" y1="18" x2="21" y2="18" />
            </svg>
          )}
        </button>
      </div>
    </div>
  );
}
