import React from "react";

const pastryProducts = [
  { id: 9, name: "Croissant", price: 3 },
  { id: 10, name: "Blueberry Muffin", price: 2.5 },
  // add more pastries here
];

export default function Pastry({ addToCart }) {
  return (
    <div>
      <h1>Pastry Menu</h1>
      <ul>
        {pastryProducts.map((product) => (
          <li key={product.id} style={{ marginBottom: "1rem" }}>
            {product.name} - ${product.price.toFixed(2)}{" "}
            <button onClick={() => addToCart({ ...product, quantity: 1 }, "pastry")}>Add to Cart</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
