import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import AddToCart from "./AddToCart";

export default function CoffeeShop({ cart, removeFromCart, updateQuantity }) {
  const [showCart, setShowCart] = useState(false);

  return (
    <div>
      {/* Your CoffeeShop content here */}

      {/* Cart Icon Button */}
      <button
        onClick={() => setShowCart(true)}
        style={{
          position: "fixed",
          top: "1rem",
          right: "1rem",
          background: "none",
          border: "none",
          cursor: "pointer",
          zIndex: 1001,
        }}
        aria-label="View Cart"
      >
        <FontAwesomeIcon icon={faShoppingCart} size="2x" />
      </button>

      {/* Show AddToCart as a modal */}
      {showCart && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            backgroundColor: "rgba(0, 0, 0, 0.5)",
            zIndex: 1000,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
          onClick={() => setShowCart(false)} // Close on backdrop click
        >
          <div
            style={{
              backgroundColor: "white",
              padding: "1rem",
              borderRadius: "8px",
              width: "95%",
              maxWidth: "800px",
              maxHeight: "90vh",
              overflowY: "auto",
            }}
            onClick={(e) => e.stopPropagation()} // Prevent modal from closing on inside click
          >
            <button
              onClick={() => setShowCart(false)}
              style={{
                position: "absolute",
                top: "10px",
                right: "20px",
                background: "none",
                border: "none",
                fontSize: "1.5rem",
                cursor: "pointer",
              }}
              aria-label="Close Cart"
            >
              âœ–
            </button>

            <AddToCart
              cart={cart}
              removeFromCart={removeFromCart}
              updateQuantity={updateQuantity}
            />
          </div>
        </div>
      )}
    </div>
  );
}
