import React from 'react';

export default function CircularText({ text, radius = 120, startAngle = -90 }) {
  const characters = text.split("");
  const degreeStep = 360 / characters.length;

  return (
    <div style={{ position: "relative", width: `${radius * 2}px`, height: `${radius * 2}px` }}>
      {characters.map((char, index) => {
        const angle = startAngle + index * degreeStep;
        const x = radius + radius * Math.cos((angle * Math.PI) / 180);
        const y = radius + radius * Math.sin((angle * Math.PI) / 180);

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${angle + 90}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none"
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}
