import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from "./assets/teacoffee.png";
import blendedImg from "./assets/chocolate.png";
import pastryImg from "./assets/pastry.png";
import CartIcon from "./CartIcon";
import CartModal from "./CartModal"; // <-- You need a cart modal/component to show cart details
import "./App.css";

export default function CoffeeShop({ addToCart, totalItems }) {
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [cartOpen, setCartOpen] = useState(false); // Track if cart is open

  const toggleMenu = () => setMenuOpen((open) => !open);
  const toggleCart = () => setCartOpen((open) => !open);

  // ... your styles here (unchanged)

  return (
    <div style={{ textAlign: "center", paddingBottom: "80px" }}>
      {/* ... your product grid unchanged */}

      {/* Floating menu bottom right */}
      <div style={floatingMenuStyle}>
        {/* Back button */}
        <button
          onClick={() => {
            navigate("/");
            setMenuOpen(false);
            setCartOpen(false);
          }}
          aria-label="Back to Home"
          title="Back to Home"
          style={{
            ...menuButtonStyle,
            width: "50px",
            height: "50px",
            background: "#eee",
            color: "#5a3e1b",
            boxShadow: "0 2px 6px rgba(0,0,0,0.15)",
            ...(!menuOpen ? hiddenStyle : visibleStyle),
          }}
        >
          {/* back arrow svg */}
        </button>

        {/* Cart icon, now clickable */}
        <div
          onClick={toggleCart} // <-- Toggle cart open/close on click
          style={{
            ...(!menuOpen ? hiddenStyle : visibleStyle),
            transitionDelay: menuOpen ? "0.1s" : "0s",
            cursor: "pointer",  // pointer cursor to show clickable
          }}
          aria-label="Open Cart"
          title="Open Cart"
          role="button"
          tabIndex={0}
          onKeyDown={(e) => { if (e.key === "Enter" || e.key === " ") toggleCart(); }}
        >
          <CartIcon totalItems={totalItems} />
        </div>

        {/* Menu toggle button */}
        <button
          onClick={toggleMenu}
          aria-label={menuOpen ? "Close menu" : "Open menu"}
          title={menuOpen ? "Close menu" : "Open menu"}
          style={menuButtonStyle}
        >
          {/* Hamburger/X SVG */}
        </button>
      </div>

      {/* Cart modal, shown only if cartOpen is true */}
      {cartOpen && (
        <CartModal
          totalItems={totalItems}
          onClose={() => setCartOpen(false)}
          addToCart={addToCart}
          // You can pass any other props needed to display cart contents
        />
      )}
    </div>
  );
}
