import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart, updateQuantity }) {
  const navigate = useNavigate();

  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items) ? items.map((item) => ({ ...item, category })) : []
  );

  const [selectedItems, setSelectedItems] = useState(new Set());
  const [swipedItems, setSwipedItems] = useState({});

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      newSet.has(key) ? newSet.delete(key) : newSet.add(key);
      return newSet;
    });
  };

  const toggleSwipe = (key) => {
    setSwipedItems((prev) => ({
      ...prev,
      [key]: !prev[key],
    }));
  };

  const handleQuantityChange = (item, newQty) => {
    const quantity = parseInt(newQty, 10);
    if (!isNaN(quantity) && quantity > 0) {
      updateQuantity(item.id, item.category, quantity);
    }
  };

  const selectedArray = allItems.filter(item =>
    selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    navigate("/payment", { state: { selectedItems: selectedArray } });

    selectedArray.forEach(item => {
      removeFromCart(item.id, item.category);
    });
  };

  const handleGoToHistory = () => {
    navigate("/history");
  };

  return (
    <div>
      {allItems.map((item) => {
        const key = `${item.category}-${item.id}`;
        const isSwiped = swipedItems[key] || false;

        return (
          <div
            key={key}
            style={{
              position: "relative",
              overflow: "hidden",
              height: "100px",
              marginBottom: "1rem",
              borderRadius: "8px",
              width: "100%",
            }}
          >
            <div
              onClick={() => removeFromCart(item.id, item.category)}
              style={{
                position: "absolute",
                left: 0,
                top: 0,
                height: "100%",
                width: "200px",
                backgroundColor: "red",
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                color: "white",
                zIndex: 0,
                fontSize: "2rem",
                borderRadius: "8px",
                cursor: "pointer",
                userSelect: "none",
                pointerEvents: isSwiped ? 'auto' : 'none',
              }}
              title="Remove item"
            >
              üóëÔ∏è
            </div>

            <li
              style={{
                margin: 0,
                padding: "1rem",
                border: "1px solid #ccc",
                borderRadius: "8px",
                display: "flex",
                alignItems: "center",
                justifyContent: "space-between",
                backgroundColor: "#fefefe",
                transform: isSwiped ? "translateX(-200px)" : "translateX(0)",
                transition: "transform 0.3s ease",
                zIndex: 1,
                position: "relative",
                height: "100%",
                cursor: "pointer",
                width: "calc(100% + 200px)",
              }}
              onClick={() => toggleSwipe(key)}
            >
              {/* Your label and input here */}
            </li>
          </div>
        );
      })}
    </div>
  );
}
