import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHouse } from '@fortawesome/free-solid-svg-icons';


export default function AddToCart({ cart, removeFromCart, updateQuantity }) {
  const navigate = useNavigate();

  // Flatten cart items with category included
  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    Array.isArray(items) ? items.map((item) => ({ ...item, category })) : []
  );

  const [selectedItems, setSelectedItems] = useState(new Set());
  const [swipedItem, setSwipedItem] = useState(null);

  const touchStartX = useRef(0);
  const touchCurrentX = useRef(0);
  const swipingKey = useRef(null);

  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(key)) newSet.delete(key);
      else newSet.add(key);
      return newSet;
    });
  };

  const handleQuantityChange = (item, newQty) => {
    const quantity = parseInt(newQty, 10);
    if (!isNaN(quantity) && quantity > 0) {
      updateQuantity(item.id, item.category, quantity);
    }
  };

  const selectedArray = allItems.filter((item) =>
    selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    navigate("/payment", { state: { selectedItems: selectedArray } });

    selectedArray.forEach((item) => {
      removeFromCart(item.id, item.category);
    });
  };

  const handleGoToHistory = () => {
    navigate("/history");
  };

  // Swipe handlers
  const handleTouchStart = (e, key) => {
    touchStartX.current = e.touches ? e.touches[0].clientX : e.clientX;
    touchCurrentX.current = touchStartX.current;
    swipingKey.current = key;
  };

  const handleTouchMove = (e) => {
    if (!touchStartX.current || !swipingKey.current) return;

    touchCurrentX.current = e.touches ? e.touches[0].clientX : e.clientX;
    const diffX = touchStartX.current - touchCurrentX.current;

    if (diffX > 50) {
      setSwipedItem(swipingKey.current);
    } else if (diffX < 0) {
      setSwipedItem(null);
    }
  };

  const handleTouchEnd = () => {
    if (!touchStartX.current || !swipingKey.current) return;

    const diffX = touchStartX.current - touchCurrentX.current;
    if (diffX > 100) {
      setSwipedItem(swipingKey.current);
    } else {
      setSwipedItem(null);
    }
    touchStartX.current = 0;
    touchCurrentX.current = 0;
    swipingKey.current = null;
  };

return (
    <div style={{ padding: "20px", maxWidth: "700px", margin: "auto" }}>
      <h1>Your Cart</h1>
      {Object.entries(cart).map(([category, items]) =>
        items.length > 0 ? (
          <div key={category} style={{ marginBottom: "2rem" }}>
            <h2 style={{ textTransform: "capitalize" }}>{category}</h2>
            {items.map((item) => (
              <div
                key={item.id}
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  marginBottom: "10px",
                  padding: "10px",
                  border: "1px solid #ccc",
                  borderRadius: "8px",
                }}
              >
                <div style={{ flex: 2 }}>
                  <strong>{item.name || "Unnamed Item"}</strong>
                </div>
                <div style={{ flex: 1 }}>
                  Price: ${((item.price ?? 0) * (item.quantity ?? 1)).toFixed(2)}
                </div>
                <div style={{ flex: 1 }}>
                  Quantity:{" "}
                  <input
                    type="number"
                    min="1"
                    value={item.quantity || 1}
                    onChange={(e) =>
                      updateQuantity(item.id, category, Math.max(1, parseInt(e.target.value, 10) || 1))
                    }
                    style={{ width: "50px" }}
                  />
                </div>
                <div style={{ flex: 0.5 }}>
                  <button
                    onClick={() => removeFromCart(item.id, category)}
                    style={{
                      background: "#d9534f",
                      color: "white",
                      border: "none",
                      borderRadius: "4px",
                      padding: "6px 12px",
                      cursor: "pointer",
                    }}
                  >
                    Remove
                  </button>
                </div>
              </div>
            ))}
          </div>
        ) : null
      )}

      {/* Show message if cart is empty */}
      {Object.values(cart).every((items) => items.length === 0) && (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
}
