import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";

import Home from "./Home";
import CoffeeShop from "./CoffeeShop";
import Espresso from "./Espresso";
import Refresher from "./Refresher";
import About from "./About";
import Service from "./Service";
import AddToCart from "./AddToCart"; 
import Payment from "./Payment";
import History from "./History";
import "./App.css";

// Navbar with dynamic link highlighting and cart count
function Navbar({ totalItems }) {
  const location = useLocation();
  const isActive = (path) => location.pathname === path;

  return (
    <nav style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}>
      <Link to="/" style={{ fontWeight: isActive("/") ? "bold" : "normal" }}>Home</Link>
      <Link to="/coffee" style={{ fontWeight: isActive("/coffee") ? "bold" : "normal" }}>Coffee Shop</Link>
      <Link to="/about" style={{ fontWeight: isActive("/about") ? "bold" : "normal" }}>About</Link>
      <Link to="/service" style={{ fontWeight: isActive("/service") ? "bold" : "normal" }}>Service</Link>
      <Link to="/history" style={{ fontWeight: isActive("/history") ? "bold" : "normal" }}>Order History</Link>
      <Link to="/cart" style={{ marginLeft: "auto" }}>
        ðŸ›’ Cart ({totalItems})
      </Link>
    </nav>
  );
}

function App() {
  // Initialize cart and history from localStorage
  const [cart, setCart] = useState(() => {
    const saved = localStorage.getItem("cart");
    return saved ? JSON.parse(saved) : { espresso: [], refresher: [] };
  });

  const [orderHistory, setOrderHistory] = useState(() => {
    const saved = localStorage.getItem("orderHistory");
    return saved ? JSON.parse(saved) : [];
  });

  // Save cart and history to localStorage on change
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem("orderHistory", JSON.stringify(orderHistory));
  }, [orderHistory]);

  // Add item to cart
  const addToCart = (item, category) => {
    setCart((prev) => {
      const updated = [...(prev[category] || [])];
      const index = updated.findIndex((i) => i.id === item.id);

      if (index !== -1) {
        updated[index].quantity += item.quantity;
      } else {
        updated.push(item);
      }

      return { ...prev, [category]: updated };
    });
  };

  // Remove item from cart
  const removeFromCart = (id, category) => {
    setCart((prev) => ({
      ...prev,
      [category]: prev[category].filter((item) => item.id !== id),
    }));
  };

  // Add multiple items to cart (e.g., from order history)
  const addMultipleToCart = (items, category) => {
    setCart((prev) => {
      let updated = [...(prev[category] || [])];

      items.forEach((item) => {
        const found = updated.find((i) => i.id === item.id);
        if (found) {
          updated = updated.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + item.quantity } : i
          );
        } else {
          updated.push(item);
        }
      });

      return { ...prev, [category]: updated };
    });
  };

  const clearCart = () => setCart({ espresso: [], refresher: [] });

  const addOrderToHistory = (order) => {
    setOrderHistory((prev) => [...prev, order]);
  };

  // Total items across both categories
  const totalItems = Object.values(cart).flat().reduce((sum, item) => sum + item.quantity, 0);

  return (
    <Router>
      <Navbar totalItems={totalItems} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/coffee" element={<CoffeeShop addToCart={addToCart} />} />
        <Route path="/espresso" element={<Espresso addToCart={addToCart} />} />
        <Route path="/refresher" element={<Refresher addToCart={addToCart} />} />
        <Route path="/about" element={<About />} />
        <Route path="/service" element={<Service />} />
        <Route path="/cart" element={<AddToCart cart={cart} removeFromCart={removeFromCart} />} />

        <Route
          path="/payment"
          element={
            <Payment
              cart={cart}
              addOrderToHistory={addOrderToHistory}
              clearCart={clearCart}
            />
          }
        />
        <Route
          path="/history"
          element={
            <History
              orderHistory={orderHistory}
              addMultipleToCart={addMultipleToCart}
            />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
