import React from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refresher.png";

export default function CoffeeShop({ cart, setCart }) {
  const navigate = useNavigate();

  const addToCart = (item, category) => {
    setCart((prev) => {
      const existingItems = prev[category] || [];
      const exists = existingItems.find((i) => i.id === item.id);

      let updatedCategory;
      if (exists) {
        updatedCategory = existingItems.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        updatedCategory = [...existingItems, { ...item, quantity: 1 }];
      }

      return { ...prev, [category]: updatedCategory };
    });
  };

  const espressoItem = {
    id: 1,
    name: "Espresso",
    price: 4,
    quantity: 1,
  };

  const refresherItem = {
    id: 2,
    name: "Refresher",
    price: 5,
    quantity: 1,
  };

  return (
    <div style={{ textAlign: "center" }}>
      <h1>Choose Your Drink</h1>
      <div style={{ display: "flex", justifyContent: "center", gap: "2rem" }}>
        <button
          onClick={() => {
            addToCart(espressoItem, "espresso");
            navigate("/espresso");
          }}
          style={{ border: "none", background: "none", cursor: "pointer" }}
        >
          <img
            src={espressoImg}
            alt="Espresso"
            style={{ width: "150px", height: "150px", borderRadius: "8px" }}
          />
          <p>Espresso</p>
        </button>

        <button
          onClick={() => {
            addToCart(refresherItem, "refresher");
            navigate("/refresher");
          }}
          style={{ border: "none", background: "none", cursor: "pointer" }}
        >
          <img
            src={refresherImg}
            alt="Refresher"
            style={{ width: "150px", height: "150px", borderRadius: "8px" }}
          />
          <p>Refresher</p>
        </button>
      </div>
    </div>
  );
}
