import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";

import Home from "./Home";
import CoffeeShop from "./CoffeeShop";
import Espresso from "./Espresso";
import Refresher from "./Refresher";
import About from "./About";
import Service from "./Service";
import AddToCart from "./AddToCart";
import Payment from "./Payment";
import History from "./History";

function Navbar({ totalItems }) {
  const location = useLocation();
  return (
    <nav style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}>
      <Link to="/" style={{ fontWeight: location.pathname === "/" ? "bold" : "normal" }}>
        Home
      </Link>
      <Link to="/coffee" style={{ fontWeight: location.pathname === "/coffee" ? "bold" : "normal" }}>
        Coffee Shop
      </Link>
      <Link to="/about" style={{ fontWeight: location.pathname === "/about" ? "bold" : "normal" }}>
        About
      </Link>
      <Link to="/service" style={{ fontWeight: location.pathname === "/service" ? "bold" : "normal" }}>
        Service
      </Link>
      <Link to="/history" style={{ fontWeight: location.pathname === "/history" ? "bold" : "normal" }}>
        Order History
      </Link>
      <Link to="/cart" style={{ marginLeft: "auto" }}>
        🛒 Cart ({totalItems})
      </Link>
    </nav>
  );
}

function App() {
  // Initialize cart and history from localStorage or empty arrays
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart ? JSON.parse(savedCart) : [];
  });

  const [orderHistory, setOrderHistory] = useState(() => {
    const savedHistory = localStorage.getItem("orderHistory");
    return savedHistory ? JSON.parse(savedHistory) : [];
  });

  // Persist cart in localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  // Persist orderHistory in localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("orderHistory", JSON.stringify(orderHistory));
  }, [orderHistory]);

  const addToCart = (item) => {
    setCart((prev) => {
      const exists = prev.find((i) => i.id === item.id);
      if (exists) {
        return prev.map((i) =>
          i.id === item.id ? { ...i, quantity: i.quantity + 1 } : i
        );
      } else {
        return [...prev, { ...item, quantity: 1 }];
      }
    });
  };

  const addMultipleToCart = (items) => {
    setCart((prev) => {
      let updatedCart = [...prev];
      items.forEach((item) => {
        const exists = updatedCart.find((i) => i.id === item.id);
        if (exists) {
          updatedCart = updatedCart.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + item.quantity } : i
          );
        } else {
          updatedCart.push(item);
        }
      });
      return updatedCart;
    });
  };

  const removeFromCart = (id) => {
    setCart((prev) => prev.filter((item) => item.id !== id));
  };

  const addOrderToHistory = (order) => {
    setOrderHistory((prev) => [...prev, order]);
  };

  // Optional: clear cart and localStorage after payment/order
  const clearCart = () => {
    setCart([]);
    localStorage.removeItem("cart");
  };

  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <Router>
      <Navbar totalItems={totalItems} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/coffee" element={<CoffeeShop />} />
        <Route path="/espresso" element={<Espresso addToCart={addToCart} />} />
        <Route path="/refresher" element={<Refresher addToCart={addToCart} />} />
        <Route path="/about" element={<About />} />
        <Route path="/service" element={<Service />} />
        <Route path="/cart" element={<AddToCart cart={cart} removeFromCart={removeFromCart} />} />
        <Route
          path="/payment"
          element={
            <Payment
              cart={cart}
              addOrderToHistory={addOrderToHistory}
              clearCart={clearCart}
            />
          }
        />
        <Route
          path="/history"
          element={<History orderHistory={orderHistory} addMultipleToCart={addMultipleToCart} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
