import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart, addOrderToHistory, clearCart }) {
  const navigate = useNavigate();

  // Flatten cart items
const allItems = Object.entries(cart).flatMap(([category, items]) =>
  Array.isArray(items)
    ? items.map((item) => ({ ...item, category }))
    : []
);


  // Track selected items by key `${category}-${id}`
  const [selectedItems, setSelectedItems] = useState(
    () => new Set(allItems.map(item => `${item.category}-${item.id}`))
  );

  // Toggle selection
  const toggleItem = (key) => {
    setSelectedItems((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(key)) {
        newSet.delete(key);
      } else {
        newSet.add(key);
      }
      return newSet;
    });
  };

  // Prepare array of selected items
  const selectedArray = allItems.filter(
    (item) => selectedItems.has(`${item.category}-${item.id}`)
  );

  const total = selectedArray.reduce(
    (sum, item) => sum + item.price * item.quantity,
    0
  );

  const handleOrderSelected = () => {
    if (selectedArray.length === 0) {
      alert("Please select at least one item to order.");
      return;
    }

    // Example: Add selected items to order history
    addOrderToHistory(selectedArray);

    // Optionally clear cart or remove ordered items from cart
    clearCart();

    alert("Order placed successfully!");
    navigate("/history"); // Or wherever you want
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Your Cart</h2>

      {allItems.length === 0 ? (
        <p>Your cart is currently empty.</p>
      ) : (
        <>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {allItems.map((item) => {
              const key = `${item.category}-${item.id}`;
              return (
                <li
                  key={key}
                  style={{
                    marginBottom: "1rem",
                    borderBottom: "1px solid #ccc",
                    paddingBottom: "0.5rem",
                  }}
                >
                  <label>
                    <input
                      type="checkbox"
                      checked={selectedItems.has(key)}
                      onChange={() => toggleItem(key)}
                    />
                    <strong>{item.name}</strong> — {item.quantity} × $
                    {item.price.toFixed(2)} ={" "}
                    <strong>${(item.price * item.quantity).toFixed(2)}</strong>
                  </label>
                  <br />
                  <button
                    style={{ marginTop: "0.5rem" }}
                    onClick={() => removeFromCart(item.id, item.category)}
                  >
                    Remove
                  </button>
                </li>
              );
            })}
          </ul>

          <h3>Total: ${total.toFixed(2)}</h3>
          <button onClick={handleOrderSelected}>Order Selected Items</button>
        </>
      )}
    </div>
  );
}
