import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";

import Home from "./Home";
import CoffeeShop from "./CoffeeShop";
import Espresso from "./Espresso";
import Refresher from "./Refresher";
import Tea from "./Tea";
import Pastry from "./Pastry";
import BlendedBeverage from "./BlendedBeverage";
import About from "./About";
import Service from "./Service";
import AddToCart from "./AddToCart";
import Payment from "./Payment";
import History from "./History";
import "./App.css";

// Navbar component
function Navbar({ totalItems }) {
  const location = useLocation();
  const showCartIcon = location.pathname !== "/cart";

  return (
    <nav style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}>
      <Link to="/" style={{ fontWeight: location.pathname === "/" ? "bold" : "normal" }}>
        Home
      </Link>
      <Link to="/coffee" style={{ fontWeight: location.pathname === "/coffee" ? "bold" : "normal" }}>
        Coffee Shop
      </Link>
      <Link to="/about" style={{ fontWeight: location.pathname === "/about" ? "bold" : "normal" }}>
        About
      </Link>
      <Link to="/service" style={{ fontWeight: location.pathname === "/service" ? "bold" : "normal" }}>
        Service
      </Link>
      <Link to="/history" style={{ fontWeight: location.pathname === "/history" ? "bold" : "normal" }}>
        Order History
      </Link>
      {showCartIcon && (
        <Link to="/cart" className="cart-link" data-count={totalItems} style={{ marginLeft: "auto" }}>
          ðŸ›’
        </Link>
      )}
    </nav>
  );
}

function App() {
  const [cartItems, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart
      ? JSON.parse(savedCart)
      : { espresso: [], refresher: [], tea: [], pastry: [], blended: [] };
  });

  const [orderHistory, setOrderHistory] = useState(() => {
    const savedHistory = localStorage.getItem("orderHistory");
    return savedHistory ? JSON.parse(savedHistory) : [];
  });

  // Persist cart and history to localStorage
  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem("orderHistory", JSON.stringify(orderHistory));
  }, [orderHistory]);

  const clearCart = () => {
    setCart({
      espresso: [],
      refresher: [],
      tea: [],
      pastry: [],
      blended: [],
    });
  };

  const addToCart = (item, category) => {
    setCart((prev) => {
      const updatedCategory = Array.isArray(prev[category]) ? [...prev[category]] : [];
      const index = updatedCategory.findIndex((i) => i.id === item.id);

      if (index !== -1) {
        updatedCategory[index] = {
          ...updatedCategory[index],
          quantity: (updatedCategory[index].quantity || 1) + (item.quantity || 1),
        };
      } else {
        updatedCategory.push({
          ...item,
          quantity: item.quantity || 1,
        });
      }

      return { ...prev, [category]: updatedCategory };
    });
  };

  const removeFromCart = (id, category) => {
    setCart((prev) => {
      const updatedCategory = prev[category].filter((item) => item.id !== id);
      return { ...prev, [category]: updatedCategory };
    });
  };

  const updateQuantity = (id, category, newQty) => {
    setCart((prev) => {
      const updatedCategory = prev[category].map((item) =>
        item.id === id ? { ...item, quantity: newQty } : item
      );
      return { ...prev, [category]: updatedCategory };
    });
  };

  const addMultipleToCart = (items, category) => {
    setCart((prev) => {
      let updatedCategory = [...(prev[category] || [])];
      items.forEach((item) => {
        const exists = updatedCategory.find((i) => i.id === item.id);
        if (exists) {
          updatedCategory = updatedCategory.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + item.quantity } : i
          );
        } else {
          updatedCategory.push(item);
        }
      });
      return { ...prev, [category]: updatedCategory };
    });
  };

  const addOrderToHistory = (items, paymentMethod = "Pay on Delivery") => {
    const newOrder = {
      id: Date.now(),
      date: new Date().toLocaleString(),
      paymentMethod,
      items,
    };

    setOrderHistory((prev) => [...prev, newOrder]);
  };

  const totalItems = 
    cartItems.reduce((sum, item) =>
     sum + item.quantity, 0);

  return (
    <Router>
      <Navbar totalItems={totalItems} />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/coffee" element={<CoffeeShop addToCart={addToCart} totalItems={totalItems} />} />

        <Route path="/espresso" element={<Espresso addToCart={addToCart} />} />
        <Route path="/refresher" element={<Refresher addToCart={addToCart} />} />
        <Route path="/tea" element={<Tea addToCart={addToCart} />} />
        <Route path="/pastry" element={<Pastry addToCart={addToCart} />} />
        <Route path="/blended" element={<BlendedBeverage addToCart={addToCart} />} />
        <Route path="/about" element={<About />} />
        <Route path="/service" element={<Service />} />
        <Route
          path="/cart"
          element={
            <AddToCart
              cart={cart}
              removeFromCart={removeFromCart}
              updateQuantity={updateQuantity}  
            />
          }
        />
        <Route
          path="/payment"
          element={
            <Payment
              cart={cart}
              addOrderToHistory={addOrderToHistory}
              clearCart={clearCart}
            />
          }
        />
        <Route
          path="/history"
          element={
            <History
              orderHistory={orderHistory}
              addMultipleToCart={addMultipleToCart}
            />
          }
        />
      </Routes>

    </Router>
  );
}

export default App;
