import React from "react";

const espressoProducts = [
  { id: 1, name: "Caffee Macchiato", price:129.99 },
  { id: 2, name: "Capuccino", price: 130.50 },
  { id: 3, name: "Mocha", price: 220.90 },
  { id: 4, name: "Latte", price: 159.00 },
];

export default function Espresso({ addToCart }) {
  return (
    <div>
      <h1>Espresso Menu</h1>
      <ul style={{
        display: "flex",
        justifyContent: "space-around"
        }}>
        {espressoProducts.map((product) => (
          <li key={product.id} style={{ 
            marginBottom: "1rem",
            background: "orangered",
            padding: "10px",
            height: "120px",
            width: "100px",
            borderRadius: "10px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
            color: "wheat"
            }}>
            {product.name} - ${product.price.toFixed(2)}{" "}
            <button 
            style ={{
              borderRadius: "50%",
              padding:"5px",
              border: "2px solid white",
              boxShadow: "0 0 2px black",
              background: "white",
              width: "35px",
              marginTop: "20px"
              }}
              onClick={() => addToCart({ ...product, quantity: 1 }, "espresso")}
            
            >
              ðŸ›’
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
