import React from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart }) {
  const navigate = useNavigate();

  // Flatten all cart items across categories and include category info
  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    items.map((item) => ({ ...item, category }))
  );

  // Calculate total price
  const total = allItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div style={{ padding: "1rem" }}>
      <h2>Your Cart</h2>

      {allItems.length === 0 ? (
        <p>Cart is empty.</p>
      ) : (
        <>
          <ul>
            {allItems.map((item) => (
              <li key={`${item.category}-${item.id}`} style={{ marginBottom: "0.5rem" }}>
                {item.name} x {item.quantity} = ${(
                  item.price * item.quantity
                ).toFixed(2)}{" "}
                <button onClick={() => removeFromCart(item.id, item.category)}>
                  Remove
                </button>
              </li>
            ))}
          </ul>

          <h3>Total: ${total.toFixed(2)}</h3>
          <button onClick={() => navigate("/payment")}>Proceed to Checkout</button>
        </>
      )}
    </div>
  );
}
