import React from "react";
import { useNavigate } from "react-router-dom";

export default function History({ orderHistory = [], addMultipleToCart, clearHistory }) {
  const navigate = useNavigate();

  const handleClearHistory = () => {
    clearHistory();
    navigate("/cart");  // redirect after clearing history
  };

  if (!Array.isArray(orderHistory) || orderHistory.length === 0) {
    return (
      <div>
        <button onClick={() => navigate("/cart")}>Back</button>
        <p>You have no past orders.</p>
      </div>
    );
  }

  return (
    <div>
      <button onClick={() => navigate("/cart")}>Back</button>
      <button
        onClick={handleClearHistory}
        style={{
          marginBottom: "1rem",
          backgroundColor: "crimson",
          color: "white",
          border: "none",
          padding: "10px 15px",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Clear History
      </button>

      <h1>Order History</h1>
      {orderHistory.map((order) => (
        <div
          key={order.id || Math.random()}
          style={{ border: "1px solid #ccc", margin: "1rem 0", padding: "1rem" }}
        >
          <p>
            <strong>Order ID:</strong> {order.id ?? "N/A"}
          </p>
          <p>
            <strong>Date:</strong> {order.date ?? "N/A"}
          </p>
          <p>
            <strong>Payment Method:</strong>{" "}
            {order.paymentMethod === "gcash" ? "Gcash" : "Pay on Delivery"}
          </p>
          <p>
            <strong>Items:</strong>
          </p>
          <ul>
            {Array.isArray(order.items) && order.items.length > 0 ? (
              order.items.map((item) => (
                <li key={item.id || Math.random()}>
                  {item.name} x {item.quantity} (${(item.price * item.quantity).toFixed(2)})
                </li>
              ))
            ) : (
              <li>No items found for this order.</li>
            )}
          </ul>
          {typeof addMultipleToCart === "function" && order.items && order.items.length > 0 && (
            <button onClick={() => addMultipleToCart(order.items)}>Order Again</button>
          )}
        </div>
      ))}
    </div>
  );
}
