import React from "react";
import { Link } from "react-router-dom";
import main from "./assets/main.jpg";
import coffeeLogo from "./assets/jasmine.png"; // your centered cup image

export default function Home() {
  return (
    <div style={{ fontFamily: "Georgia, serif" }}>
      {/* Navbar */}
      <nav style={{ display: "flex", justifyContent: "center", gap: "2rem", margin: "1rem 0", fontWeight: "bold", fontSize: "18px" }}>
        <Link to="/" style={{ textDecoration: "none", color: "#5a3e36" }}>Home</Link>
        <Link to="/about" style={{ textDecoration: "none", color: "#5a3e36" }}>About Us</Link>
        <Link to="/products" style={{ textDecoration: "none", color: "#5a3e36" }}>Products</Link>
        <Link to="/contact" style={{ textDecoration: "none", color: "#5a3e36" }}>Contacts</Link>
      </nav>

      {/* Main Content */}
      <div style={{ display: "flex", justifyContent: "center", gap: "2rem", padding: "1rem 2rem" }}>
        {/* Left: Coffee shop image */}
        <img
          src={main}
          alt="Coffee Shop Interior"
          style={{
            width: "400px",
            height: "auto",
            borderRadius: "10px",
          }}
        />

        {/* Right: Logo */}
        <div style={{ position: "relative", width: "300px", height: "300px", textAlign: "center" }}>
          <div style={{
            position: "absolute",
            top: "0",
            left: "0",
            width: "100%",
            transform: "translateY(-40px) rotate(-10deg)",
            fontSize: "22px",
          }}>
            The Daily grind
          </div>
          <img
            src={coffeeLogo}
            alt="Coffee Logo"
            style={{
              width: "150px",
              height: "150px",
              borderRadius: "50%",
              marginTop: "70px"
            }}
          />
          <div style={{
            position: "absolute",
            bottom: "0",
            left: "0",
            width: "100%",
            transform: "translateY(40px) rotate(10deg)",
            fontSize: "22px",
          }}>
            Coffee house
          </div>
        </div>
      </div>

      {/* Order Box */}
      <div style={{
        background: "linear-gradient(to right, #d9c4a7, #c0a87c)",
        borderRadius: "20px",
        padding: "2rem",
        maxWidth: "700px",
        margin: "2rem auto",
        textAlign: "center",
        color: "#3b2e2a"
      }}>
        <p>
          When placing an order, select the option <br />
          <strong>"Contactless delivery"</strong> and the courier will leave your order at the door.
        </p>
        <Link to="/coffee">
          <button style={{
            padding: "10px 20px",
            background: "#947848",
            color: "white",
            border: "none",
            borderRadius: "5px",
            marginTop: "1rem",
            fontWeight: "bold",
            cursor: "pointer"
          }}>
            ORDER NOW
          </button>
        </Link>
      </div>
    </div>
  );
}
