import React from "react";

export default function CircularText({ text, radius = 120, startAngle = 0, endAngle = 180 }) {
  const characters = text.split("");
  const angleRange = endAngle - startAngle;
  const degreeStep = angleRange / (characters.length - 1); // spread along arc

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        const angle = startAngle + index * degreeStep;
        const rad = (angle * Math.PI) / 180;

        // x,y position on circle edge
        const x = radius + radius * Math.cos(rad);
        const y = radius + radius * Math.sin(rad);

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(0deg)`, // no letter rotation
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}
