import React from "react";

const blendedProducts = [
  { id: 7, name: "Vanilla Frappe", price: 4.5 },
  { id: 8, name: "Mocha Frappe", price: 5 },
  // add more blended drinks here
];

export default function BlendedBeverage({ addToCart }) {
  return (
    <div>
      <h1>Blended Beverage Menu</h1>
      <ul>
        {blendedProducts.map((product) => (
          <li key={product.id} style={{ marginBottom: "1rem" }}>
            {product.name} - ${product.price.toFixed(2)}{" "}
            <button onClick={() => addToCart({ ...product, quantity: 1 }, "blended")}>Add to Cart</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
