import React from "react";
import { useNavigate } from "react-router-dom";

export default function AddToCart({ cart, removeFromCart }) {
  const navigate = useNavigate();

  const allItems = Object.entries(cart).flatMap(([category, items]) =>
    items.map((item) => ({ ...item, category }))
  );

  const total = allItems.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Your Cart</h2>

      {allItems.length === 0 ? (
        <p>Your cart is currently empty.</p>
      ) : (
        <>
          <ul style={{ listStyle: "none", padding: 0 }}>
            {allItems.map((item) => (
              <li
                key={`${item.category}-${item.id}`}
                style={{
                  marginBottom: "1rem",
                  borderBottom: "1px solid #ccc",
                  paddingBottom: "0.5rem",
                }}
              >
                <strong>{item.name}</strong> — {item.quantity} × ${item.price.toFixed(2)} ={" "}
                <strong>${(item.price * item.quantity).toFixed(2)}</strong>
                <br />
                <button
                  style={{ marginTop: "0.5rem" }}
                  onClick={() => removeFromCart(item.id, item.category)}
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>

          <h3>Total: ${total.toFixed(2)}</h3>
          <button onClick={() => navigate("/payment")}>Proceed to Checkout</button>
        </>
      )}
    </div>
  );
}
