import React from "react";

function CircularText({ text, radius, startAngle, endAngle, invert }) {
  const characters = text.split("");
  const totalAngle = Math.abs(endAngle - startAngle);
  const degreeStep = totalAngle / (characters.length - 1);

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        const angle =
          startAngle < endAngle
            ? startAngle + index * degreeStep
            : startAngle - index * degreeStep;

        const rad = (angle * Math.PI) / 180;

        const x = radius + radius * Math.cos(rad);
        const y = radius - radius * Math.sin(rad);

        const rotation = invert ? angle + 270 : angle - 90;

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${rotation}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}

export function TopCircularText({ text = "the daily grind", radius = 120 }) {
  return (
    <CircularText
      text={text}
      radius={radius}
      startAngle={180}
      endAngle={0}
      invert={false}
    />
  );
}

export function BottomCircularText({ text = "coffee house", radius = 120 }) {
  const adjustedRadius = radius * 0.85; // smaller radius to bring bottom text closer

  return (
    <CircularText
      text={text}
      radius={adjustedRadius}
      startAngle={180}
      endAngle={360}
      invert={true}
    />
  );
}
