import React from "react";
import { useNavigate } from "react-router-dom";
import espressoImg from "./assets/espresso.png";
import refresherImg from "./assets/refreshercoffee.png";
import teaImg from './assets/teacoffee.png';
import blendedImg from './assets/chocolate.png';
import pastryImg from './assets/pastry.png';
import {useLocation} from "react-router-dom";
export default function CoffeeShop() {
  const navigate = useNavigate();
  const location = useLocation();

  
  return (
    <div style={{ textAlign: "center" }}>
      <h1>Choose Your Coffee</h1>
      <div style={{ 
        display: "grid", 
  gridTemplateColumns: "repeat(3, 1fr)", 
  gap: "1rem", 
  justifyItems: "center", 
  maxWidth: "600px",
  margin: "2rem auto",
  textAlign: "center"
        }}>
        <button
          onClick={() => navigate("/espresso")}
          style={{
            border: "none",
            cursor: "pointer",
            background: "none",
            textAlign: "center",
          }}
        >
          <img
            src={espressoImg}
            alt="Espresso"
            style={{ }}
          />
          <div>Espresso</div>
        </button>
        <button 
          onClick={() => navigate("/tea")} 
          style={{
            border: "none",
            cursor: "pointer",
            background: "none",
            textAlign: "center",
          }}
          >
            <img 
              src={teaImg} 
              alt="Tea" 
              style={{width: "150px", height: "150px", borderRadius: "10px" }} 
              />
          <div>Tea</div>
        </button>
        <button 
          onClick={() => navigate("/blended")} 
          style={{
            border: "none",
            cursor: "pointer",
            background: "none",
            textAlign: "center",
          }}
          >
            <img 
              src={blendedImg} 
              alt="Tea" 
              style={{width: "100px", height: "150px", borderRadius: "10px" }} 
              />
          <div>Blended Beverage</div>
        </button>

        <button
          onClick={() => navigate("/refresher")}
          style={{
            border: "none",
            cursor: "pointer",
            background: "none",
            textAlign: "center",
          }}
        >
          <img
            src={refresherImg}
            alt="Refresher"
            style={{ width: "120px", height: "150px", borderRadius: "10px" }}
          />
          <div>Refresher</div>
        </button>

        <button
          onClick={() => navigate("/pastry")}
          style={{
            border: "none",
            cursor: "pointer",
            background: "none",
            textAlign: "center",
          }}
        >
          <img
            src={pastryImg}
            alt="Pastry"
            style={{ width: "140px", height: "150px", borderRadius: "10px" }}
          />
          <div>Pastry</div>
        </button>
      </div>
    </div>
  );
}
