import React from "react";

function CircularText({ text, radius = 120, startAngle, endAngle, invert }) {
  const characters = text.split("");
  const totalAngle = Math.abs(endAngle - startAngle);
  const degreeStep = totalAngle / (characters.length - 1);

  return (
    <div
      style={{
        position: "relative",
        width: `${radius * 2}px`,
        height: `${radius * 2}px`,
      }}
    >
      {characters.map((char, index) => {
        // Calculate angle from startAngle towards endAngle (left to right)
        const angle =
          startAngle < endAngle
            ? startAngle + index * degreeStep
            : startAngle - index * degreeStep;

        const rad = (angle * Math.PI) / 180;

        // x, y positions on the circle edge
        const x = radius + radius * Math.cos(rad);

        // y calculation:
        // For top arc: y is above center (radius - sin)
        // For bottom arc: y is below center (radius - sin), sin negative means y > radius
        const y = radius - radius * Math.sin(rad);

        // Rotation angle so text is upright
        // For top arc (invert=false): rotate(angle - 90)
        // For bottom arc (invert=true): rotate(angle + 90 + 180) to flip letters upright
        const rotation = invert ? angle + 270 : angle - 90;

        return (
          <span
            key={index}
            style={{
              position: "absolute",
              left: `${x}px`,
              top: `${y}px`,
              transform: `translate(-50%, -50%) rotate(${rotation}deg)`,
              fontSize: "20px",
              fontFamily: "Georgia, serif",
              color: "#5a3e36",
              userSelect: "none",
            }}
          >
            {char}
          </span>
        );
      })}
    </div>
  );
}
