import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useLocation,
} from "react-router-dom";

import Home from "./Home";
import CoffeeShop from "./CoffeeShop";
import Espresso from "./Espresso";
import Refresher from "./Refresher";
import About from "./About";
import Service from "./Service";
import AddToCart from "./AddToCart";
import Payment from "./Payment";
import History from "./History";
import "./App.css";

function Navbar({ totalItems }) {
  const location = useLocation();
  return (
    <nav
      style={{ display: "flex", gap: "1rem", padding: "1rem", background: "#eee" }}
    >
      <Link to="/" style={{ fontWeight: location.pathname === "/" ? "bold" : "normal" }}>
        Home
      </Link>
      <Link
        to="/coffee"
        style={{ fontWeight: location.pathname === "/coffee" ? "bold" : "normal" }}
      >
        Coffee Shop
      </Link>
      <Link
        to="/about"
        style={{ fontWeight: location.pathname === "/about" ? "bold" : "normal" }}
      >
        About
      </Link>
      <Link
        to="/service"
        style={{ fontWeight: location.pathname === "/service" ? "bold" : "normal" }}
      >
        Service
      </Link>
      <Link
        to="/history"
        style={{ fontWeight: location.pathname === "/history" ? "bold" : "normal" }}
      >
        Order History
      </Link>
      <Link to="/cart" style={{ marginLeft: "auto" }}>
        ðŸ›’ Cart ({totalItems})
      </Link>
    </nav>
  );
}

function App() {
  // Initialize cart with espresso and refresher arrays, each item should have quantity
  const [cart, setCart] = useState(() => {
    const savedCart = localStorage.getItem("cart");
    return savedCart ? JSON.parse(savedCart) : { espresso: [], refresher: [] };
  });

  const [orderHistory, setOrderHistory] = useState(() => {
    const savedHistory = localStorage.getItem("orderHistory");
    return savedHistory ? JSON.parse(savedHistory) : [];
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  useEffect(() => {
    localStorage.setItem("orderHistory", JSON.stringify(orderHistory));
  }, [orderHistory]);

  // Add item to cart with category (espresso or refresher)
  const addToCart = (item, category) => {
    setCart((prev) => {
      const updatedCategory = [...(prev[category] || [])];
      const index = updatedCategory.findIndex((i) => i.id === item.id);

      if (index !== -1) {
        // Item exists: increase quantity
        updatedCategory[index] = {
          ...updatedCategory[index],
          quantity: updatedCategory[index].quantity + item.quantity,
        };
      } else {
        // New item: add with quantity
        updatedCategory.push(item);
      }

      return { ...prev, [category]: updatedCategory };
    });
  };

  // Remove item from cart
  const removeFromCart = (id, category) => {
    setCart((prev) => {
      const updatedCategory = prev[category].filter((item) => item.id !== id);
      return { ...prev, [category]: updatedCategory };
    });
  };

  // Add multiple items at once to cart, used in history for reordering
  const addMultipleToCart = (items, category) => {
    setCart((prev) => {
      let updatedCategory = [...(prev[category] || [])];
      items.forEach((item) => {
        const exists = updatedCategory.find((i) => i.id === item.id);
        if (exists) {
          updatedCategory = updatedCategory.map((i) =>
            i.id === item.id ? { ...i, quantity: i.quantity + item.quantity } : i
          );
        } else {
          updatedCategory.push(item);
        }
      });
      return { ...prev, [category]: updatedCategory };
    });
  };

  const addOrderToHistory = (order) => {
    setOrderHistory((prev) => [...prev, order]);
  };

  // Calculate total items in cart (sum of all quantities)
  const totalItems = Object.values(cart)
    .flat()
    .reduce((sum, item) => sum + item.quantity, 0);

  return (
    <Router>
      <Navbar totalItems={totalItems} />
      <Routes>
        <Route path="/" element={<Home />} />
        {/* Pass addToCart with correct signature */}
        <Route path="/coffee" element={<CoffeeShop addToCart={addToCart} />} />
        <Route path="/espresso" element={<Espresso addToCart={addToCart} />} />
        <Route path="/refresher" element={<Refresher addToCart={addToCart} />} />
        <Route path="/about" element={<About />} />
        <Route path="/service" element={<Service />} />
        <Route
          path="/cart"
          element={<AddToCart cart={cart} removeFromCart={removeFromCart} />}
        />
        <Route
          path="/payment"
          element={
            <Payment
              cart={cart}
              addOrderToHistory={addOrderToHistory}
              clearCart={() => setCart({ espresso: [], refresher: [] })}
            />
          }
        />
        <Route
          path="/history"
          element={
            <History
              orderHistory={orderHistory}
              addMultipleToCart={addMultipleToCart}
            />
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
